import React, { useState, useEffect, useMemo } from "react";
import { MapContainer, TileLayer, useMap, Marker, Popup, Polyline } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import SearchForm from "./SearchForm";
import L from "leaflet";
import { useLocation } from "./LocationContext";
import Button from "@mui/material/Button";

// üéØ Predefined colors for different providers
const lineColors = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

// üü¢ Custom icon for user location
const userIcon = L.icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
    iconSize: [35, 35],
    iconAnchor: [17, 35]
});

// üéØ Function to create color-matched clinic/MVZ/etc. icons
const getColoredProviderIcon = (color) => {
    return L.divIcon({
        className: "custom-clinic-marker",
        html: `<div style="
            width: 30px; height: 30px; 
            background-color: ${color}; 
            border-radius: 50%; 
            border: 1px solid black;
            display: flex; justify-content: center; align-items: center;">
        </div>`,
        iconSize: [20, 20],
        iconAnchor: [15, 20]
    });
};

// ‚úÖ AutoZoom component (now fully dynamic & functional)
const AutoZoom = ({ locations }) => {
    const map = useMap();

    useEffect(() => {
        // üéØ Filter only locations with valid latitude & longitude
        const validLocations = locations.filter(loc => loc.latitude && loc.longitude);

        if (validLocations.length > 0) {
            const bounds = L.latLngBounds(validLocations.map(loc => [loc.latitude, loc.longitude]));
            map.fitBounds(bounds, { padding: [50, 50] }); // Ensure all points are visible
        } else {
            console.warn("‚ö†Ô∏è No valid locations found for AutoZoom!");
        }
    }, [locations, map]);

    return null; // No UI component, only logic
};

const Map = () => {
    const [graphData, setGraphData] = useState([]);  // Store clinic/MVZ/etc. data
    const [userLocation, setUserLocation] = useState(null);  // User location
    const [locationChoice, setLocationChoice] = useState("");  
    const [manualAddress, setManualAddress] = useState("");  
    const { setAllLocations } = useLocation();

    // üöÄ Fetch user's current location
    const fetchMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;

                    if (lat && lon) {
                        setUserLocation({
                            latitude: lat,
                            longitude: lon
                        });
                        console.log("‚úÖ User location set:", lat, lon);
                    } else {
                        console.warn("‚ö†Ô∏è Invalid user location received.");
                    }
                },
                (error) => {
                    console.error("‚ùå Error getting user location:", error);
                }
            );
        }
    };

    // üéØ Handle location selection
    const handleLocationChoice = (choice) => {
        setLocationChoice(choice);
        setUserLocation(null);
        setManualAddress(""); 
        if (choice === "my-location") {
            fetchMyLocation();
        }
    };

    // üéØ Convert address to latitude & longitude using OpenStreetMap's API
    const handleManualAddressSearch = async () => {
        if (!manualAddress.trim()) {
            alert("Please enter a valid address.");
            return;
        }
        try {
            const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(manualAddress)}`);
            const data = await response.json();
            if (data.length > 0 && data[0].lat && data[0].lon) {
                setUserLocation({
                    latitude: parseFloat(data[0].lat),
                    longitude: parseFloat(data[0].lon)
                });
            } else {
                alert("Address not found. Please enter a more precise location.");
                console.warn("‚ùå No valid latitude/longitude found in API response.");
            }
        } catch (error) {
            console.error("Error fetching geolocation:", error);
            alert("Failed to fetch location. Try again later.");
        }
    };

    // ‚úÖ Combine all locations (graph data + user location) dynamically
    const allLocations = useMemo(() => {
        const locations = [...graphData];  // Start with clinic/MVZ/etc. data
        if (userLocation) {
            locations.push({ name: "Your Location", provider_type: "User", ...userLocation });
        }
        return locations;
    }, [graphData, userLocation]);

    // ‚úÖ Update locations when changed
    useEffect(() => {
        setAllLocations(allLocations);
    }, [allLocations, setAllLocations]);

    return (
        <div style={{
            backgroundColor: "#f0ffff", // Light blue background
            border: "1px solid #b3e5fc",
            borderRadius: "10px",
            padding: "10px",
            marginBottom: "15px",
            boxShadow: "0px 2px 10px rgba(0,0,0,0.1)"
        }}>
        <h3>W√§hlen Sie Ihren Standort:</h3>
        <div style={{ display: "flex", gap: "10px", marginBottom: "10px", paddingLeft: "10px" }}>
            <Button variant="contained" color="primary" onClick={() => handleLocationChoice("my-location")}>Meinen Standort verwenden</Button>
            <Button variant="contained" color="primary" onClick={() => handleLocationChoice("manual")}>Standort manuell eingeben</Button>
        </div>

        {locationChoice === "manual" && (
            <div style={{ paddingLeft: "10px",marginBottom: "10px" }}>
                <input 
                    type="text" 
                    placeholder="Geben Sie Ihre Adresse ein" 
                    value={manualAddress} 
                    onChange={(e) => setManualAddress(e.target.value)}
                    style={{ padding: "8px", width: "250px", border: "1px solid #ccc", borderRadius: "5px", marginRight: "10px" }} 
                />
                <button 
                    onClick={handleManualAddressSearch} 
                    style={{
                        padding: "8px 12px",
                        borderRadius: "5px",
                        backgroundColor: "#007BFF",
                        color: "white",
                        border: "none",
                        cursor: "pointer",
                        fontSize: "14px",
                        fontWeight: "bold",
                        transition: "background-color 0.3s"
                    }}
                    onMouseOver={(e) => e.target.style.backgroundColor = "#0056b3"}
                    onMouseOut={(e) => e.target.style.backgroundColor = "#007BFF"}
                >
                    Search Address
                </button>
            </div>
        )}

        <SearchForm setGraphData={setGraphData} />

        <MapContainer center={[50.851, 8.015]} zoom={8} scrollWheelZoom={false} style={{ height: "500px", width: "100%", borderRadius: "8px" }}>
            <TileLayer
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            />

            <AutoZoom locations={allLocations} />

            {allLocations.map((data, index) => (
                <Marker key={index} position={[data.latitude, data.longitude]} icon={getColoredProviderIcon(lineColors[index % lineColors.length])}>
                    <Popup>
                        <strong>{data.name}</strong> <br />
                        <strong>City:</strong> {data.city || "N/A"} <br />
                        <strong>Address:</strong> {data.address || "N/A"}
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
        </div>
    );
};

export default Map;
