import React, { useState, useEffect, useMemo, useCallback } from "react";
import { useLocation } from "./LocationContext";

// Constants
const LINE_COLORS = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

// Styles
const styles = {
  informationsContainer: {
    width: "35%",
    fontWeight: "600",
    position: "absolute",
    border: "2px solid #b3e5fc",
    right: "10px",
    top: "130px",
    overflowY: "auto",
    maxHeight: "200px",
    padding: "15px",
    backgroundColor: "#e1ecf0",
    borderRadius: "8px",
    fontSize: "12px"
  },
  analysticContainer: {
    width: "35%",
    fontWeight: "600",
    position: "absolute",
    border: "2px solid #b3e5fc",
    right: "10px",
    top: "350px",
    overflowY: "auto",
    maxHeight: "635px",
    padding: "15px",
    backgroundColor: "#e1ecf0",
    borderRadius: "8px",
    fontSize: "12px"
  },
  title: {
    fontSize: "18px",
    marginBottom: "8px",
    fontWeight: "600"
  },
  list: {
    listStyleType: "none",
    padding: 0,
    margin: 0
  },
  listItem: {
    marginBottom: "8px",
    padding: "8px",
    borderBottom: "1px solid #ddd"
  },
  bold: {
    fontWeight: "600"
  },
  noData: {
    fontStyle: "italic",
    color: "#999"
  },
  colorDot: {
    display: "inline-block",
    width: "12px",
    height: "12px",
    borderRadius: "50%",
    marginRight: "5px"
  },
  calculatingText: {
    color: "#666",
    fontStyle: "italic"
  }
};

// Helper functions
const getProviderKey = (type, name, address) => {
  return `${type}-${(name || "").trim()}-${(address || "").trim()}`;
};

const getRoadDistance = async (startLat, startLon, endLat, endLon, attempt = 1) => {
  if (!startLat || !startLon || !endLat || !endLon) return null;

  // Add a small delay between requests to avoid rate limiting
  await new Promise(resolve => setTimeout(resolve, attempt * 200));

  try {
    const response = await fetch(
      `https://router.project-osrm.org/route/v1/driving/${startLon},${startLat};${endLon},${endLat}?overview=false`
    );
    
    if (!response.ok) {
      if (response.status === 429 && attempt < 3) {
        // Exponential backoff for rate limiting
        return getRoadDistance(startLat, startLon, endLat, endLon, attempt + 1);
      }
      return null;
    }

    const data = await response.json();
    return data.routes?.[0]?.distance ? (data.routes[0].distance / 1000).toFixed(2) : null;
  } catch (err) {
    console.error("Distance fetch error:", err);
    return null;
  }
};

const getProviderDetails = (provider) => {
  const type = provider.clinic_name ? "clinic" :
    provider.mvz_name ? "mvz" :
    provider.asv_name ? "asv" :
    provider.niedergelassene_name ? "niedergelassene" :
    provider.neurologe_name ? "neurologe" :
    provider.onkologe_name ? "onkologe" : null;

  const name = provider.clinic_name || provider.mvz_name || provider.asv_name || 
    provider.niedergelassene_name || provider.neurologe_name || provider.onkologe_name;

  const address = provider.clinic_address || provider.mvz_address || provider.asv_address || 
    provider.niedergelassene_address || provider.neurologe_address || provider.onkologe_address;

  const city = provider.clinic_city || provider.mvz_city || provider.asv_city || 
    provider.niedergelassene_city || provider.neurologe_city || provider.onkologe_city;

  const lat = provider.clinic_latitude || provider.mvz_latitude || provider.asv_latitude || 
    provider.niedergelassene_latitude || provider.neurologe_latitude || provider.onkologe_latitude;

  const lon = provider.clinic_longitude || provider.mvz_longitude || provider.asv_longitude || 
    provider.niedergelassene_longitude || provider.neurologe_longitude || provider.onkologe_longitude;

  return { type, name, address, city, lat, lon };
};

const Analytics = React.memo(() => {
  const [distances, setDistances] = useState({});
  const [isCalculating, setIsCalculating] = useState(false);
  const { allLocations, selectedLocation, userLocation } = useLocation();


    // const originLocation = useMemo(() => {
    //     return userLocation || allLocations.find(loc => 
    //     loc.clinic_name === "Your Location" || loc.clinic_name === "Zentrum (Umkreis)"
    //     );
    // }, [userLocation, allLocations]);

        const originLocation = useMemo(() => {
            return allLocations.find(loc => 
            loc.clinic_name === "Your Location" || loc.clinic_name === "Zentrum (Umkreis)"
            );
        }, [allLocations]);
      

  const fetchDistances = useCallback(async () => {
    if (!originLocation?.latitude || !originLocation?.longitude) return;

    setIsCalculating(true);
    const newDistances = {};
    
    // Process locations in batches to avoid overwhelming the API/browser
    const batchSize = 5;
    const batches = [];
    
    for (let i = 0; i < allLocations.length; i += batchSize) {
      batches.push(allLocations.slice(i, i + batchSize));
    }

    for (const batch of batches) {
      const batchPromises = batch.map(provider => {
        if (provider.clinic_name === "Your Location" || provider.clinic_name === "Zentrum (Umkreis)") {
          return Promise.resolve();
        }

        const { type, name, address, lat, lon } = getProviderDetails(provider);
        if (!type || !name || !lat || !lon) return Promise.resolve();

        const key = getProviderKey(type, name, address);
        return getRoadDistance(originLocation.latitude, originLocation.longitude, lat, lon)
          .then(distance => { 
            if (distance !== null) {
              newDistances[key] = distance;
            }
          });
      });

      await Promise.all(batchPromises);
      
      // Update state after each batch to show partial progress
      setDistances(prev => ({ ...prev, ...newDistances }));
      
      // Small delay between batches
      await new Promise(resolve => setTimeout(resolve, 500));
    }

    setIsCalculating(false);
  }, [originLocation, allLocations]);

  useEffect(() => {
    fetchDistances();
  }, [fetchDistances]);

  const selectedDetails = useMemo(() => {
    if (!selectedLocation) return null;
    return getProviderDetails(selectedLocation);
  }, [selectedLocation]);

  const selectedDistanceKey = useMemo(() => {
    if (!selectedDetails) return null;
    return getProviderKey(selectedDetails.type, selectedDetails.name, selectedDetails.address);
  }, [selectedDetails]);

  const filteredLocations = useMemo(() => {
    return allLocations.filter(provider => 
      provider.clinic_name !== "Your Location" && 
      provider.clinic_name !== "Zentrum (Umkreis)"
    );
  }, [allLocations]);

  const renderDistance = (key) => {
    if (!key) return <span style={styles.calculatingText}>Nicht verf체gbar</span>;
    if (key in distances) return <span>{distances[key]} km</span>;
    return <span style={styles.calculatingText}>Berechne...</span>;
  };

  const renderSelectedLocationInfo = useMemo(() => {
    if (!selectedDetails) {
      return <p style={{ fontStyle: "italic", color: "#666" }}>Keine Einrichtung ausgew채hlt.</p>;
    }

    return (
      <>
        {selectedDetails.name && <p><strong>Name:</strong> {selectedDetails.name}</p>}
        {selectedDetails.address && <p><strong>Adresse:</strong> {selectedDetails.address}</p>}
        {selectedDetails.city && <p><strong>Stadt:</strong> {selectedDetails.city}</p>}
        <p>
          <strong>Beste Route:</strong>{" "}
          <span style={styles.bold}>
            {renderDistance(selectedDistanceKey)}
          </span>
        </p>
      </>
    );
  }, [selectedDetails, selectedDistanceKey, distances]);

  const renderProviderList = useMemo(() => {
    if (filteredLocations.length === 0) {
      return <p style={styles.noData}>Keine Daten verf체gbar.</p>;
    }

    return (
      <ul style={styles.list}>
        {filteredLocations.map((provider, index) => {
          const { type, name, address, city } = getProviderDetails(provider);
          if (!type || !name) return null;

          const key = getProviderKey(type, name, address);

          return (
            <li key={`provider-${index}`} style={styles.listItem}>
              <span style={{ ...styles.colorDot, backgroundColor: LINE_COLORS[index % LINE_COLORS.length] }} />
              <strong>{name}</strong><br />
              {address && <><strong>Adresse:</strong> {address}<br /></>}
              {city && <><strong>Stadt:</strong> {city}<br /></>}
              <strong>Beste Route:</strong>{" "}
              <span style={styles.bold}>
                {renderDistance(key)}
              </span>
            </li>
          );
        })}
      </ul>
    );
  }, [filteredLocations, distances]);

  return (
    <>
      <div style={styles.informationsContainer}>
        <h3 style={styles.title}>Informationen zur ausgew채hlten Einrichtung</h3>
        {renderSelectedLocationInfo}
      </div>

      <div style={styles.analysticContainer}>
        <h3 style={styles.title}>Analytics Dashboard</h3>
        {isCalculating && filteredLocations.length > 0 && (
          <p style={styles.calculatingText}>Berechne Entfernungen...</p>
        )}
        {renderProviderList}
      </div>
      <div style={styles.analysticContainer}>
        <h3 style={styles.title}>Analytics Dashboard</h3>
        {isCalculating && filteredLocations.length > 0 && (
          <p style={styles.calculatingText}>Berechne Entfernungen...</p>
        )}
        {renderProviderList}
      </div>
    </>
  );
});

export default Analytics;
