import React, { useState, useEffect, useMemo } from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import SearchForm from "./SearchForm";
import L from "leaflet";
import { useLocation } from "./LocationContext";
import Button from '@mui/material/Button';

// üé® Custom marker icons
const lineColors = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

const userIcon = L.icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
    iconSize: [35, 35],
    iconAnchor: [17, 35]
});

const getColoredClinicIcon = (color) => {
    return L.divIcon({
        className: "custom-clinic-marker",
        html: `<div style="width: 30px; height: 30px; background-color: ${color}; border-radius: 50%; border: 1px solid black;"></div>`,
        iconSize: [20, 20],
        iconAnchor: [15, 20]
    });
};

// üîç AutoZoom to fit all points
const AutoZoom = ({ locations }) => {
    const map = useMap();

    useEffect(() => {
        const validLocations = locations.filter(loc =>
            loc.latitude && loc.longitude
        );

        if (validLocations.length > 0) {
            const bounds = L.latLngBounds(validLocations.map(loc => [loc.latitude, loc.longitude]));
            map.fitBounds(bounds, { padding: [50, 50] });
        }
    }, [locations, map]);

    return null;
};

const Map = () => {
    const [graphData, setGraphData] = useState([]);
    const [userLocation, setUserLocation] = useState(null);
    const [locationChoice, setLocationChoice] = useState("");
    const [manualAddress, setManualAddress] = useState("");
    const { setAllLocations, selectedLocation, setSelectedLocation } = useLocation();

    // üìç Fetch user's current location
    const fetchMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;

                    setUserLocation({ latitude: lat, longitude: lon });
                },
                (error) => {
                    console.error("Geolocation error:", error);
                }
            );
        }
    };

    const handleMarkerClick = (location) => {
        setSelectedLocation(location);
    };

    const handleLocationChoice = (choice) => {
        setLocationChoice(choice);
        setUserLocation(null);
        setManualAddress("");
        if (choice === "my-location") {
            fetchMyLocation();
        }
    };

    // üåç Manual address to lat/lng
    const handleManualAddressSearch = async () => {
        if (!manualAddress.trim()) return;

        try {
            const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(manualAddress)}`);
            const data = await response.json();
            if (data.length > 0) {
                setUserLocation({
                    latitude: parseFloat(data[0].lat),
                    longitude: parseFloat(data[0].lon)
                });
            } else {
                alert("Adresse nicht gefunden.");
            }
        } catch (error) {
            console.error("Geocoding error:", error);
        }
    };

    // üß≠ Decide which origin to connect lines from
    const getOriginLocation = () => {
        if (userLocation) {
            return userLocation;
        } else if (graphData.length > 0 && graphData[0].search_center_lat && graphData[0].search_center_lon) {
            return {
                latitude: graphData[0].search_center_lat,
                longitude: graphData[0].search_center_lon,
                label: "Zentrum (Umkreis)"
            };
        }
        return null;
    };

    // üì¶ All points shown on map (providers + origin)
    const allLocations = useMemo(() => {
        const locations = [...graphData];
        const origin = getOriginLocation();
        if (origin) {
            locations.push({
                clinic_name: origin.label || "Your Location",
                provider_type: "Origin",
                latitude: origin.latitude,
                longitude: origin.longitude
            });
        }
        return locations;
    }, [graphData, userLocation]);

    useEffect(() => {
        setAllLocations(allLocations);
    }, [allLocations, setAllLocations]);

    return (
        <div style={{ backgroundColor: "#f0ffff", padding: "10px", borderRadius: "10px" }}>
            <h3>W√§hlen Sie Ihren Standort:</h3>
            <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                <Button variant="contained" onClick={() => handleLocationChoice("my-location")}>Meinen Standort verwenden</Button>
                <Button variant="contained" onClick={() => handleLocationChoice("manual")}>Standort manuell eingeben</Button>
            </div>

            {locationChoice === "manual" && (
                <div>
                    <input
                        type="text"
                        placeholder="Adresse eingeben"
                        value={manualAddress}
                        onChange={(e) => setManualAddress(e.target.value)}
                        style={{ padding: "8px", width: "250px", marginRight: "10px" }}
                    />
                    <button
                        onClick={handleManualAddressSearch}
                        style={{
                            padding: "8px 12px",
                            borderRadius: "5px",
                            backgroundColor: "#007BFF",
                            color: "white",
                            border: "none",
                            cursor: "pointer"
                        }}
                    >
                        Search Address
                    </button>
                </div>
            )}

            <SearchForm setGraphData={setGraphData} />

            <div style={{ width: "59%", height: "500px", marginTop: "15px", backgroundColor: "white", borderRadius: "10px", boxShadow: "0px 4px 10px rgba(0,0,0,0.15)" }}>
                <MapContainer center={[50.851, 8.015]} zoom={8} scrollWheelZoom={false} style={{ height: "100%", width: "100%", borderRadius: "8px" }}>
                    <TileLayer
                        attribution='&copy; OpenStreetMap contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />
                    <AutoZoom locations={allLocations} />

                    {/* üîµ Origin marker */}
                    {getOriginLocation() && (
                        <Marker
                            position={[getOriginLocation().latitude, getOriginLocation().longitude]}
                            icon={userIcon}
                        >
                            <Popup>{getOriginLocation().label || "Your Location"}</Popup>
                        </Marker>
                    )}

                    {/* üî¥ Provider markers */}
                    {graphData.map((data, index) => {
                        const lat = data.clinic_latitude || data.mvz_latitude || data.neurologe_latitude || data.onkologe_latitude;
                        const lon = data.clinic_longitude || data.mvz_longitude || data.neurologe_longitude || data.onkologe_longitude;
                        if (!lat || !lon) return null;

                        const color = lineColors[index % lineColors.length];

                        return (
                            <Marker
                                key={index}
                                position={[lat, lon]}
                                icon={getColoredClinicIcon(color)}
                                eventHandlers={{ click: () => handleMarkerClick(data) }}
                            >
                                <Popup>
                                    <strong>{data.clinic_name || data.mvz_name || data.neurologe_name || data.onkologe_name}</strong><br />
                                    Stadt: {data.clinic_city || data.mvz_city || data.neurologe_city || data.onkologe_city}<br />
                                    Adresse: {data.clinic_address || data.mvz_address || data.neurologe_address || data.onkologe_address}<br />
                                    Betten: {data.clinic_number_of_beds || "N/A"}
                                </Popup>
                            </Marker>
                        );
                    })}

                    {/* üü¢ Draw lines from origin to providers */}
                    {(() => {
                        const origin = getOriginLocation();
                        if (!origin) return null;

                        return graphData.map((data, index) => {
                            const lat = data.clinic_latitude || data.mvz_latitude || data.neurologe_latitude || data.onkologe_latitude;
                            const lon = data.clinic_longitude || data.mvz_longitude || data.neurologe_longitude || data.onkologe_longitude;
                            if (!lat || !lon) return null;

                            const color = lineColors[index % lineColors.length];

                            return (
                                <Polyline
                                    key={index}
                                    positions={[[origin.latitude, origin.longitude], [lat, lon]]}
                                    color={color}
                                    weight={2}
                                />
                            );
                        });
                    })()}
                </MapContainer>
            </div>
        </div>
    );
};

export default Map;
