import { useState } from "react";
import axios from "axios";

const SearchForm = ({ setGraphData }) => {
    // üü¢ State for search fields
    const [city, setCity] = useState("");
    const [icdCode, setIcdCode] = useState("");
    const [radius, setRadius] = useState(30); // Default 30 km

    // üü¢ State for filters
    const [focus, setFocus] = useState({
        oncology: true,
        neurology: false,
    });
    const [providers, setProviders] = useState({
        clinics: true,
        mvz: true,
        asv: true,
        niedergelasseneAertze: false,
    });
    const [cooperation, setCooperation] = useState(false);

    // üü¢ Function to validate input before searching
    const isValidSearch = () => {
        const hasTextInput = city.trim() !== "" || icdCode.trim() !== ""; // Check if City or ICD Code has input
        const hasSelectedFilters =
            Object.values(focus).some(Boolean) || Object.values(providers).some(Boolean) || cooperation; // Check if any filter is selected

        return hasTextInput || hasSelectedFilters; // Allow search if at least one input or filter is selected
    };

    // üü¢ Handle search request
    const handleSearch = async (e) => {
        e.preventDefault();

        if (!isValidSearch()) {
            alert("‚ö†Ô∏è Please enter at least one field or select a filter before searching.");
            return;
        }

        console.log("üîµ Search Button Clicked");

        // Convert selected checkboxes into arrays for API request
        const selectedFocus = Object.keys(focus).filter((key) => focus[key]);
        const selectedProviders = Object.keys(providers).filter((key) => providers[key]);

        try {
            console.log("üîµ Sending API request...");

            const response = await axios.get("http://127.0.0.1:5000/search", {
                params: {
                    city,
                    icd_code: icdCode,
                    radius,
                    focus: selectedFocus.join(","), // Convert to comma-separated string
                    providers: selectedProviders.join(","),
                    cooperation,
                },
            });

            console.log("‚úÖ API Response Received:", response.data);

            if (response.data && response.data.length > 0) {
                setGraphData(response.data); // ‚úÖ Update data if valid response
            } else {
                console.warn("‚ö†Ô∏è No data found for the given filters.");
                setGraphData([]); // Clear data if empty response
            }
        } catch (error) {
            console.error("‚ùå API Error:", error);
            alert("Error fetching data. Please try again.");
        }
    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleSearch} style={styles.form}>
                
                {/* üü¢ FLEX CONTAINER FOR SEARCH FIELDS & GUIDELINE BOX */}
                <div style={styles.searchRow}>
                    
                    {/* üü¢ SEARCH FIELDS (CITY & ICD CODE) */}
                    <div style={styles.inputContainer}>
                        <input
                            type="text"
                            placeholder="City"
                            value={city}
                            onChange={(e) => setCity(e.target.value)}
                            style={styles.input}
                        />
                        <input
                            type="text"
                            placeholder="ICD Code"
                            value={icdCode}
                            onChange={(e) => setIcdCode(e.target.value)}
                            style={styles.input}
                        />
                    </div>
                    
                    {/* üü¢ GUIDELINE BOX */}
                    <div style={styles.guideBox}>
                        <strong>Search Guidelines:</strong>
                        <ul style={{ paddingLeft: "15px", marginTop: "5px" }}>
                            <li>Enter a **City** to filter locations.</li>
                            <li>Use an **ICD Code** to filter by diagnosis.</li>
                            <li>Adjust **radius (Umkreis)** to set search range.</li>
                            <li>Use **Schwerpunkt & Leistungserbringer** for advanced filters.</li>
                            <li>Toggle **Kooperationen** to show collaborations.</li>
                        </ul>
                    </div>
                </div>

                {/* üü¢ RADIUS INPUT */}
                <div style={styles.radiusContainer}>
                    <label>Umkreis:</label>
                    <input
                        type="number"
                        value={radius}
                        onChange={(e) => setRadius(e.target.value)}
                        style={styles.radiusInput}
                    />
                    <span>km</span>
                </div>

                {/* üü¢ FILTERS (Schwerpunkt & Leistungserbringer in same line) */}
                <div style={styles.filterRow}>
                    
                    {/* üü¢ SCHWERPUNKT FILTER */}
                    <div style={styles.filterBox}>
                        <h4>Schwerpunkt</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={focus.oncology}
                                onChange={() => setFocus({ ...focus, oncology: !focus.oncology })}
                            />
                            Onkologie
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={focus.neurology}
                                onChange={() => setFocus({ ...focus, neurology: !focus.neurology })}
                            />
                            Neurologie
                        </label>
                    </div>

                    {/* üü¢ LEISTUNGSERBRINGER FILTER (Same Line) */}
                    <div style={styles.filterBox}>
                        <h4>Leistungserbringer</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.clinics}
                                onChange={() => setProviders({ ...providers, clinics: !providers.clinics })}
                            />
                            Kliniken
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.mvz}
                                onChange={() => setProviders({ ...providers, mvz: !providers.mvz })}
                            />
                            MVZ
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.asv}
                                onChange={() => setProviders({ ...providers, asv: !providers.asv })}
                            />
                            ASV
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.niedergelasseneAertze}
                                onChange={() => setProviders({ ...providers, niedergelasseneAertze: !providers.niedergelasseneAertze })}
                            />
                            Niedergelassene √Ñrzte
                        </label>
                    </div>
                </div>

                {/* üü¢ KOOPERATIONEN TOGGLE */}
                <div style={styles.toggleContainer}>
                    <label>Kooperationen</label>
                    <input
                        type="checkbox"
                        checked={cooperation}
                        onChange={() => setCooperation(!cooperation)}
                        style={styles.toggleSwitch}
                    />
                </div>

                {/* üü¢ SEARCH BUTTON */}
                <button type="submit" style={styles.searchButton}>Search</button>
            </form>
        </div>
    );
};

export default SearchForm;
