  return (
    <div>
      <div style={{
        backgroundColor: "#f9f9f9", 
        border: "2px solid #b3e5fc",
        borderRadius: "10px",
        padding: "10px",
        textAlign: "center",
        boxShadow: "2px 2px 5px rgba(0,0,0,0.1)",
        margin: "20px auto",  // Center horizontally while reducing space
        width: "95%",  // Make it take up more width
        maxWidth: "1450px"  // Limit maximum width for responsiveness
      }}>
        <h1 style={{ fontSize: "24px", fontWeight: "bold" }}>
          Herzlich Willkommen zu neuen Wegen der Versorgung durch Analyse von Patientenstr√∂men!
        </h1>
      </div>

      <LocationProvider>
        <Map />
        <Analystics />
      </LocationProvider>
    </div>
  );
}


export default App;
