import { useState } from "react";
import axios from "axios";

const SearchForm = ({ setGraphData }) => {
    // üü¢ State for search fields
    const [city, setCity] = useState("");
    const [icdCode, setIcdCode] = useState("");
    const [radius, setRadius] = useState(30); 

    // üü¢ State for filters
    const [focus, setFocus] = useState({
        oncology: false,
        neurology: false,
    });
    const [providers, setProviders] = useState({
        clinics: false,
        mvz: false,
        asv: false,
        niedergelasseneAertze: false,
    });
    const [cooperation, setCooperation] = useState(false);

    // üü¢ Handle Search Button Click
    const handleSearch = async (e) => {
        e.preventDefault();
        console.log("üîµ Search Button Clicked");

        // ‚úÖ Ensure at least one field or checkbox is selected
        const hasInput = city || icdCode || Object.values(focus).includes(true) || Object.values(providers).includes(true);
        if (!hasInput) {
            alert("‚ö†Ô∏è Please enter a city, ICD code, or select at least one filter.");
            return;
        }

        try {
            console.log(`üîµ Sending request to API...`);
            const response = await axios.get("http://127.0.0.1:5000/search", {
                params: { city, icd_code: icdCode, radius, focus, providers, cooperation },
            });

            console.log("‚úÖ API Response Received:", response.data);

            if (response.data.length > 0) {
                setGraphData(response.data);  
            } else {
                console.warn("‚ö†Ô∏è No data returned from API");
            }
        } catch (error) {
            console.error("‚ùå API Error:", error);
            alert("Error fetching data. Please try again.");
        }
    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleSearch} style={styles.form}>
                {/* üü¢ Search Inputs */}
                <div style={styles.searchRow}>
                    <input type="text" placeholder="City" value={city} onChange={(e) => setCity(e.target.value)} style={styles.input} />
                    <input type="text" placeholder="ICD Code" value={icdCode} onChange={(e) => setIcdCode(e.target.value)} style={styles.input} />
                    <div style={styles.guideBox}><strong>Search Guidelines</strong></div>
                </div>

                {/* üü¢ Radius Input */}
                <div style={styles.radiusContainer}>
                    <label>Umkreis</label>
                    <input type="number" value={radius} onChange={(e) => setRadius(e.target.value)} style={styles.radiusInput} />
                    <span>km</span>
                </div>

                {/* üü¢ Filters (Schwerpunkt & Leistungserbringer in same row) */}
                <div style={styles.filterRow}>
                    <div style={styles.filterBox}>
                        <h4>Schwerpunkt</h4>
                        <label><input type="checkbox" checked={focus.oncology} onChange={() => setFocus({ ...focus, oncology: !focus.oncology })} /> Onkologie</label>
                        <label><input type="checkbox" checked={focus.neurology} onChange={() => setFocus({ ...focus, neurology: !focus.neurology })} /> Neurologie</label>
                    </div>
                    <div style={styles.filterBox}>
                        <h4>Leistungserbringer</h4>
                        <label><input type="checkbox" checked={providers.clinics} onChange={() => setProviders({ ...providers, clinics: !providers.clinics })} /> Kliniken</label>
                        <label><input type="checkbox" checked={providers.mvz} onChange={() => setProviders({ ...providers, mvz: !providers.mvz })} /> MVZ</label>
                        <label><input type="checkbox" checked={providers.asv} onChange={() => setProviders({ ...providers, asv: !providers.asv })} /> ASV</label>
                        <label><input type="checkbox" checked={providers.niedergelasseneAertze} onChange={() => setProviders({ ...providers, niedergelasseneAertze: !providers.niedergelasseneAertze })} /> Niedergelassene √Ñrzte</label>
                    </div>
                </div>

                {/* üü¢ Kooperationen Toggle */}
                <div style={styles.toggleContainer}>
                    <label>Kooperationen</label>
                    <input type="checkbox" checked={cooperation} onChange={() => setCooperation(!cooperation)} />
                </div>

                {/* üü¢ Search Button */}
                <button type="submit" style={styles.searchButton}>Search</button>
            </form>
        </div>
    );
};

// üü¢ Styles
const styles = {
    container: { width: "60%", padding: "10px", backgroundColor: "#f9f9f9", borderRadius: "8px", marginBottom: "10px" },
    form: { display: "flex", flexDirection: "column", gap: "10px", width: "100%" },
    searchRow: { display: "flex", justifyContent: "space-between", alignItems: "center", width: "100%" },
    input: { fontSize: "12px", padding: "5px", width: "100%" },
    guideBox: { backgroundColor: "#e3f2fd", padding: "10px", borderRadius: "5px", fontSize: "12px" },
    radiusContainer: { display: "flex", alignItems: "center", gap: "5px", fontSize: "12px" },
    filterRow: { display: "flex", justifyContent: "space-between", gap: "20px" },
    filterBox: { backgroundColor: "#fff", padding: "10px", borderRadius: "5px", fontSize: "12px" },
    toggleContainer: { display: "flex", alignItems: "center", gap: "10px", fontSize: "12px" },
    searchButton: { padding: "10px", backgroundColor: "#007BFF", color: "#fff", border: "none", cursor: "pointer", fontSize: "12px" },
};

export default SearchForm;
