import React, { useState, useEffect, useMemo } from "react";
import { useLocation } from "./LocationContext";

// üéØ Predefined colors for providers
const lineColors = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

// üöÄ Function to get road distance from OSRM API
const getRoadDistance = async (startLat, startLon, endLat, endLon) => {
    if (!startLat || !startLon || !endLat || !endLon) {
        console.warn("üö® Missing coordinates for distance calculation!", { startLat, startLon, endLat, endLon });
        return "N/A";
    }

    const url = `https://router.project-osrm.org/route/v1/driving/${startLon},${startLat};${endLon},${endLat}?overview=false`;

    try {
        const response = await fetch(url);
        const data = await response.json();
        if (data.routes && data.routes.length > 0) {
            return (data.routes[0].distance / 1000).toFixed(2); // Convert meters to km
        } else {
            console.warn("‚ö†Ô∏è No valid route found for coordinates:", { startLat, startLon, endLat, endLon });
            return "N/A";
        }
    } catch (error) {
        console.error("‚ùå Error fetching road distance:", error);
        return "N/A";
    }
};

const Analystics = () => {
    // const { allLocations } = useLocation();
    const [distances, setDistances] = useState({});
    const { allLocations, selectedLocation } = useLocation();

    // // ‚úÖ Extract Unique ICD Information (Only Once)
    // const uniqueICDInfo = useMemo(() => {
    //     return allLocations.length > 0
    //         ? { 

    //             icd_name: allLocations[0].icd_name || "N/A",
    //             icd_fallzahl: allLocations[0].icd_fallzahl || "N/A"
    //         } 
    //         : null;
    // }, [allLocations]);

    // ‚úÖ Find User's Location
    const userLocation = useMemo(() => 
        allLocations.find(loc => loc.clinic_name === "Your Location"), 
        [allLocations]
    );

    // ‚úÖ Fetch Road Distances for ALL PROVIDERS
    useEffect(() => {
        const fetchDistances = async () => {
            if (!userLocation || !userLocation.latitude || !userLocation.longitude) {
                console.warn("‚ö†Ô∏è No valid user location found!");
                return;
            }

            const newDistances = {};

            for (const provider of allLocations) {
                if (provider.clinic_name === "Your Location") continue; // Skip user location

                // üîπ Identify the provider type dynamically
                const providerType = provider.clinic_name 
                    ? "clinic"
                    : provider.mvz_name 
                    ? "mvz"
                    : provider.asv_name 
                    ? "asv"
                    : provider.niedergelassene_name 
                    ? "niedergelassene"
                    : provider.neurologe_name
                    ? "neurologe"
                    : provider.onkologe_name
                    ? "onkologe"
                    : null;

                const providerName = provider.clinic_name || provider.mvz_name || provider.asv_name || provider.niedergelassene_name || provider.neurologe_name || provider.onkologe_name;

                if (!providerType || !providerName) continue;

                const providerLat = provider.clinic_latitude || provider.mvz_latitude || provider.asv_latitude || provider.niedergelassene_latitude || provider.neurologe_latitude || provider.onkologe_latitude;
                const providerLon = provider.clinic_longitude || provider.mvz_longitude || provider.asv_longitude || provider.niedergelassene_longitude || provider.neurologe_longitude || provider.onkologe_longitude;

                if (!providerLat || !providerLon) {
                    console.warn(`‚ö†Ô∏è Skipping provider with missing coordinates: ${providerName}`);
                    continue;
                }

                // üîπ Fetch the road distance
                const roadDistance = await getRoadDistance(
                    userLocation.latitude, userLocation.longitude, 
                    providerLat, providerLon
                );

                // üîπ Store distance dynamically
                newDistances[`${providerType}-${providerName}`] = roadDistance;
            }

            setDistances(newDistances);
        };

        fetchDistances();
    }, [userLocation, allLocations]);
    const selectedProviderType = selectedLocation?.clinic_name
        ? "clinic"
        : selectedLocation?.mvz_name
        ? "mvz"
        : selectedLocation?.asv_name
        ? "asv"
        : selectedLocation?.niedergelassene_name
        ? "niedergelassene"
        : null;

    const selectedProviderName = selectedLocation?.clinic_name || 
                                selectedLocation?.mvz_name || 
                                selectedLocation?.asv_name || 
                                selectedLocation?.niedergelassene_name || 
                                selectedLocation?.neurologe_name || 
                                selectedLocation?.onkologe_name;

    return (
       <>
        <div style={styles.informationsContainer}>
            <h3 style={styles.title}>Informationen zur ausgew√§hlten Einrichtung</h3>
                
            {selectedLocation ? (
                <>
                {selectedLocation.clinic_name || selectedLocation.mvz_name || selectedLocation.neurologe_name || selectedLocation.onkologe_name ? (
                    <p><strong>Name:</strong> {selectedLocation.clinic_name || selectedLocation.mvz_name || selectedLocation.neurologe_name || selectedLocation.onkologe_name}</p>
                ) : null}
            
                {selectedLocation.clinic_address || selectedLocation.mvz_address || selectedLocation.neurologe_address || selectedLocation.onkologe_address ? (
                    <p><strong>Adresse:</strong> {selectedLocation.clinic_address || selectedLocation.mvz_address || selectedLocation.neurologe_address || selectedLocation.onkologe_address}</p>
                ) : null}
            
                {selectedLocation.clinic_city || selectedLocation.mvz_city || selectedLocation.neurologe_city || selectedLocation.onkologe_city ? (
                    <p><strong>Stadt:</strong> {selectedLocation.clinic_city || selectedLocation.mvz_city || selectedLocation.neurologe_city || selectedLocation.onkologe_city}</p>
                ) : null}
            
                {selectedLocation.clinic_cooperation || selectedLocation.mvz_cooperation || selectedLocation.asv_cooperation || selectedLocation.neurologe_cooperation || selectedLocation.onkologe_cooperation ? (
                    <p><strong>Kooperation:</strong> {selectedLocation.clinic_cooperation || selectedLocation.mvz_cooperation || selectedLocation.asv_cooperation || selectedLocation.neurologe_cooperation || selectedLocation.onkologe_cooperation}</p>
                ) : null}
            
                <p><strong>Beste Route:</strong> <span style={styles.bold}>
                    {distances[`${selectedProviderType}-${selectedProviderName}`] || "Berechne..."} km
                </span></p>
            </>
            ) : (
                <p style={{ fontStyle: "italic", color: "#666" }}>
                    Keine Einrichtung ausgew√§hlt.
                </p>
            )}
        </div>

            {/* üìå Leistungserbringer (Providers) Information */}
            <div style={styles.analysticContainer}>
                <h3 style={styles.title}>Leistungserbringer Informationen</h3>
                {allLocations.length > 0 ? (
                    <ul style={styles.list}>
                        {allLocations.map((provider, index) => {
                            if (provider.clinic_name === "Your Location") return null; // Skip user location

                            // üîπ Dynamically determine provider type and name
                            const providerType = provider.clinic_name 
                                ? "clinic"
                                : provider.mvz_name 
                                ? "mvz"
                                : provider.asv_name 
                                ? "asv"
                                : provider.niedergelassene_name 
                                ? "niedergelassene"
                                : provider.neurologe_name
                                ? "neurologe"
                                : provider.onkologe_name
                                ? "onkologe"
                                : null;

                            // const providerName = provider.clinic_name || provider.mvz_name || provider.asv_name || provider.niedergelassene_name;
                            // üîπ Determine provider name
                            const providerName =
                                provider.clinic_name ||
                                provider.mvz_name ||
                                provider.asv_name ||
                                provider.niedergelassene_name ||
                                provider.neurologe_name ||
                                provider.onkologe_name;

                            if (!providerType || !providerName) return null;

                            return (
                                <li key={index} style={styles.listItem}>
                                    {/* üîµ Colored Circle Indicator */}
                                    <span 
                                        style={{
                                            color: lineColors[index % lineColors.length], 
                                            fontSize: "14px",  
                                            marginRight: "5px",
                                            display: "inline-block",
                                            width: "12px",
                                            height: "12px",
                                            borderRadius: "50%",
                                            backgroundColor: lineColors[index % lineColors.length],
                                        }}>
                                    </span>

                                    <strong>{providerName}</strong> <br />

                                    {(provider.clinic_city || provider.mvz_city || provider.asv_city || provider.niedergelassene_city || provider.onkologe_city || provider.neurologe_city ) && (
                                        <><strong>Stadt:</strong> {provider.clinic_city || provider.mvz_city || provider.asv_city || provider.niedergelassene_city || provider.onkologe_city || provider.neurologe_city} <br /></>
                                    )}

                                    {(provider.clinic_address || provider.mvz_address || provider.asv_address || provider.niedergelassene_address || provider.onkologe_address|| provider.neurologe_address) && (
                                        <><strong>Adresse:</strong> {provider.clinic_address || provider.mvz_address || provider.asv_address || provider.niedergelassene_address || provider.onkologe_address|| provider.neurologe_address} <br /></>
                                    )}

                                    {provider.clinic_number_of_beds && (
                                        <><strong>Anzahl von Betten:</strong> {provider.clinic_number_of_beds} <br /></>
                                    )}

                                    {(provider.clinic_cooperation || provider.mvz_cooperation || provider.asv_cooperation || provider.neurologe_cooperation || provider.onkologe_cooperation ) && (
                                        <><strong>Kooperation:</strong> {provider.clinic_cooperation || provider.mvz_cooperation || provider.asv_cooperation || provider.neurologe_cooperation || provider.onkologe_cooperation } <br /></>
                                    )}

                                    {provider.clinic_chefarzt && (
                                        <><strong>Chefarzt:</strong> {provider.clinic_chefarzt} <br /></>
                                    )}

                                    <strong>Beste Route:</strong> 
                                    <span style={styles.bold}>
                                        {distances[`${providerType}-${providerName}`] || "Berechne..."} km
                                    </span>
                                </li>
                            );
                        })}
                    </ul>
                ) : (
                    <p style={styles.noData}>Keine Daten verf√ºgbar.</p>
                )}
            </div>
        </>
    );
};
