import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, useMap, Marker, Popup, Polyline } from 'react-leaflet'
import 'leaflet/dist/leaflet.css'
import SearchForm from "./SearchForm";
import L from "leaflet";


// Custom icon for user location
const userIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
    iconSize: [30, 30],
    iconAnchor: [15, 30]
});

// Auto-zoom component
const AutoZoom = ({ locations }) => {
    const map = useMap(); // Access the map instance

    useEffect(() => {
        if (locations.length > 0) {
            const bounds = L.latLngBounds(locations.map(loc => [loc.latitude, loc.longitude]));
            map.fitBounds(bounds, { padding: [50, 50] }); // Adjust padding for better view
        }
    }, [locations, map]);

    return null; // This component only handles map logic
};
const Map = () => {

    const [latitude, setlatitude] = useState(0);
    const [longitude, setlongitude] = useState(0);
    const [graphData, setGraphData] = useState([]);
    const [userLocation, setUserLocation] = useState(null);


    console.log(graphData[0]);
    // setlatitude(graphData[0].latitude);
    // setlongitude(graphData[0].longitude);
    useEffect(() => {
        if (graphData.length > 0) {
            graphData.forEach((data, index) => {
                console.log(`Position ${index + 1}: Latitude ${data.latitude}, Longitude ${data.longitude}`);
                setlatitude(data.latitude);
                setlongitude(data.longitude);
            });
            
        }
    }, [graphData]);  // Runs when graphData updates
    
    const position = [latitude, longitude];
    console.log(position);

   // Function to get user's current location
   useEffect(() => {
    if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
            (position) => {
                setUserLocation({
                    latitude: position.coords.latitude,
                    longitude: position.coords.longitude
                });
            },
            (error) => {
                console.error("Error getting location:", error);
            }
        );
    }
}, []);

  // Combine user location with other markers
  const allLocations = [...graphData];
  if (userLocation) {
      allLocations.push({ clinic_name: "Your Location", ...userLocation });
  }

  return (
    <div>
      <h1>Healthcare Data Visualization with GraphXR</h1>
      <SearchForm setGraphData={setGraphData} />
      <MapContainer center={[50.851, 8.015]} zoom={8} scrollWheelZoom={false} style={{height: 900}}>
    <TileLayer
      attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    />
    {/* Auto-Zoom Feature */}
    <AutoZoom locations={allLocations} />
    {/* <Marker position={position} >
      <Popup>
        A pretty CSS3 popup. <br /> Easily customizable.
      </Popup>
    </Marker> */}
    {graphData.map((data, index) => (
                <Marker key={index} position={[data.latitude, data.longitude]}>
                    <Popup>
                        <strong>{data.clinic_name}</strong><br />
                        City: {data.city} <br />
                        Lat: {data.latitude}, Lng: {data.longitude}
                    </Popup>
                </Marker>

            ))}

            {/* Show User's Current Location */}
            {userLocation && (
                    <Marker position={[userLocation.latitude, userLocation.longitude]} icon={userIcon}>
                        <Popup>
                            <strong>Your Location</strong><br />
                            Lat: {userLocation.latitude}, Lng: {userLocation.longitude}
                        </Popup>
                    </Marker>
                )}

                  {/* Draw lines from user to each clinic */}
                  {userLocation && graphData.map((data, index) => (
                    <Polyline 
                        key={index} 
                        positions={[[userLocation.latitude, userLocation.longitude], [data.latitude, data.longitude]]} 
                        color="blue" 
                    />
                ))}
  </MapContainer>,
    </div>
  );
};

export default Map;
