import { useState } from "react";
import axios from "axios";

const SearchForm = ({ setGraphData }) => {
    const [city, setCity] = useState("");
    const [icdCode, setIcdCode] = useState("");
    const [radius, setRadius] = useState(30); // Default 30 km
    const [focus, setFocus] = useState({
        oncology: true,
        neurology: false,
    });
    const [providers, setProviders] = useState({
        clinics: true,
        mvz: true,
        asv: true,
        niedergelasseneAertze: false,
    });
    const [cooperation, setCooperation] = useState(false);

    const handleSearch = async (e) => {
        e.preventDefault();
        console.log("üîµ Search Button Clicked");

        try {
            console.log(`üîµ Sending request to: http://127.0.0.1:5000/search`);
            
            const response = await axios.get("http://127.0.0.1:5000/search", {
                params: {
                    city,
                    icd_code: icdCode,
                    radius,
                    focus: Object.keys(focus).filter((key) => focus[key]), // Get selected focus
                    providers: Object.keys(providers).filter((key) => providers[key]), // Get selected providers
                    cooperation,
                },
            });

            console.log("‚úÖ API Response Received:", response.data);
            setGraphData(response.data);
        } catch (error) {
            console.error("‚ùå API Error:", error);
        }
    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleSearch} style={styles.form}>
                {/* üü¢ FLEX CONTAINER FOR SEARCH FIELDS & COMMENT BOX */}
                <div style={styles.commentContainer}>
                    <div style={styles.inputContainer}>
                        <input
                            type="text"
                            placeholder="City"
                            value={city}
                            onChange={(e) => setCity(e.target.value)}
                            style={styles.input}
                        />
                        <input
                            type="text"
                            placeholder="ICD Code"
                            value={icdCode}
                            onChange={(e) => setIcdCode(e.target.value)}
                            style={styles.input}
                        />
                    </div>
                    <textarea
                        placeholder="Add a comment..."
                        style={styles.commentBox}
                    />
                </div>
    
                <div style={styles.radiusContainer}>
                    <label>Umkreis:</label>
                    <input
                        type="number"
                        value={radius}
                        onChange={(e) => setRadius(e.target.value)}
                        style={styles.radiusInput}
                    />
                    <span>km</span>
                </div>
    
                <div style={styles.filterRow}>
                    <div style={styles.filterBox}>
                        <h4>Schwerpunkt</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={focus.oncology}
                                onChange={() => setFocus({ ...focus, oncology: !focus.oncology })}
                            />
                            Onkologie
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={focus.neurology}
                                onChange={() => setFocus({ ...focus, neurology: !focus.neurology })}
                            />
                            Neurologie
                        </label>
                    </div>
    
                    <div style={styles.filterBox}>
                        <h4>Leistungserbringer</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.clinics}
                                onChange={() => setProviders({ ...providers, clinics: !providers.clinics })}
                            />
                            Kliniken
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.mvz}
                                onChange={() => setProviders({ ...providers, mvz: !providers.mvz })}
                            />
                            MVZ
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.asv}
                                onChange={() => setProviders({ ...providers, asv: !providers.asv })}
                            />
                            ASV
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.niedergelasseneAertze}
                                onChange={() => setProviders({ ...providers, niedergelasseneAertze: !providers.niedergelasseneAertze })}
                            />
                            Niedergelassene √Ñrzte
                        </label>
                    </div>
                </div>
    
                <div style={styles.toggleContainer}>
                    <label>Kooperationen</label>
                    <input
                        type="checkbox"
                        checked={cooperation}
                        onChange={() => setCooperation(!cooperation)}
                        style={styles.toggleSwitch}
                    />
                </div>
    
                <button type="submit" style={styles.searchButton}>Search</button>
            </form>
        </div>
    );
    };
    const styles = {
        container: {
            padding: "10px",
            borderRadius: "8px",
            backgroundColor: "#f9f9f9",
            width: "60%",
            height: "500px",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
            alignItems: "center",
            boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
            overflow: "auto",
            marginBottom: "10px",
            fontSize: "12px",
        },
        form: {
            display: "flex",
            flexDirection: "column",
            gap: "10px",
            width: "100%",
        },
        /* üü¢ REDUCE SPACE BETWEEN SEARCH FIELDS */
        inputContainer: {
            display: "flex",
            justifyContent: "space-between",
            gap: "5px",  // üü¢ Reduce gap between inputs
            alignItems: "center",
        },
        input: {
            fontSize: "12px",
            padding: "5px",
            width: "100%",
        },
        radiusContainer: {
            display: "flex",
            alignItems: "center",
            gap: "5px",
            fontSize: "12px",
        },
        filterRow: {
            display: "flex",
            justifyContent: "space-between",
            alignItems: "flex-start",
            gap: "20px",
        },
        filterBox: {
            flex: 1,
            backgroundColor: "#fff",
            padding: "10px",
            borderRadius: "5px",
            boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.1)",
            fontSize: "12px",
        },
        toggleContainer: {
            display: "flex",
            alignItems: "center",
            gap: "10px",
            marginTop: "5px",
            fontSize: "12px",
        },
        /* üü¢ COMMENT BOX STYLES */
        commentContainer: {
            width: "100%",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginTop: "5px",
        },
        commentBox: {
            width: "30%",
            height: "50px",
            padding: "5px",
            fontSize: "12px",
            border: "1px solid #ccc",
            borderRadius: "5px",
            resize: "none",
        },
        searchButton: {
            padding: "10px",
            backgroundColor: "#007BFF",
            color: "#fff",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            width: "100%",
            fontSize: "12px",
        },
    };
    


export default SearchForm;
