import React, { useState, useEffect } from "react";
import { useLocation } from "./LocationContext";

const Analystics = () => {
    const { allLocations } = useLocation();
    const [distances, setDistances] = useState({});

    // Function to dynamically generate provider info
    const renderProviderInfo = (provider, index) => {
        return (
            <li key={index} style={styles.listItem}>
                {/* Colored Circle */}
                <span
                    style={{
                        color: lineColors[index % lineColors.length],
                        fontSize: "14px",
                        marginRight: "5px",
                        display: "inline-block",
                        width: "12px",
                        height: "12px",
                        borderRadius: "50%",
                        backgroundColor: lineColors[index % lineColors.length],
                    }}>
                </span>
                <strong>{provider.clinic_name || provider.mvz_name || provider.neurologe_name || provider.onkologe_name}</strong> <br />

                {provider.clinic_city || provider.mvz_city || provider.neurologe_city || provider.onkologe_city ? (
                    <><strong>Stadt:</strong> {provider.clinic_city || provider.mvz_city || provider.neurologe_city || provider.onkologe_city} <br /></>
                ) : null}

                {provider.clinic_chefarzt ? (
                    <><strong>Chefarzt:</strong> {provider.clinic_chefarzt} <br /></>
                ) : null}

                {provider.clinic_address || provider.mvz_address || provider.neurologe_address || provider.onkologe_address ? (
                    <><strong>Adresse:</strong> {provider.clinic_address || provider.mvz_address || provider.neurologe_address || provider.onkologe_address} <br /></>
                ) : null}

                {provider.clinic_number_of_beds ? (
                    <><strong>Anzahl von Betten:</strong> {provider.clinic_number_of_beds} <br /></>
                ) : null}

                {distances[provider.clinic_name] ? (
                    <><strong>Entfernung:</strong> <span style={styles.bold}>{distances[provider.clinic_name]} km</span><br /></>
                ) : (
                    <><strong>Entfernung:</strong> <span style={styles.bold}>Berechnung läuft...</span><br /></>
                )}
            </li>
        );
    };

    return (
        <div style={styles.analysticContainer}>
            <h3 style={styles.title}>Leistungserbringer Informationen</h3>
            {allLocations.length > 0 ? (
                <ul style={styles.list}>
                    {allLocations.map((provider, index) => (
                        renderProviderInfo(provider, index)
                    ))}
                </ul>
            ) : (
                <p style={styles.noData}>Keine Daten verfügbar.</p>
            )}
        </div>
    );
};

// ✅ Define Styling
const styles = {
    analysticContainer: {
        width: "35%",
        fontWeight: "600",
        position: "absolute",
        right: "10px",
        top: "235px",
        padding: "15px",
        backgroundColor: "#d0ebff",
        boxShadow: "0px 0px 8px rgba(0, 0, 0, 0.1)",
        borderRadius: "8px",
        fontSize: "12px",
    },
    title: {
