        margin: 0
    },
    listItem: {
        marginBottom: "8px",
        padding: "8px",
        borderBottom: "1px solid #ddd"
    },
    bold: {
        fontWeight: "600"
    },
    noData: {
        fontStyle: "italic",
        color: "#999"
    }
};

