import React, { useState, useEffect } from "react";
import { useLocation } from "./LocationContext";
import axios from "axios";

// Function to get road distance from OSRM API
const getRoadDistance = async (lat1, lon1, lat2, lon2) => {
    const url = `https://router.project-osrm.org/route/v1/driving/${lon1},${lat1};${lon2},${lat2}?overview=false`;

    try {
        const response = await fetch(url);
        const data = await response.json();
        if (data.routes && data.routes.length > 0) {
            return (data.routes[0].distance / 1000).toFixed(2); // Convert meters to km
        } else {
            return "N/A";
        }
    } catch (error) {
        console.error("âŒ Error fetching road distance:", error);
        return "N/A";
    }
};

const Analystics = () => {
    const { allLocations } = useLocation();
    const [distances, setDistances] = useState({});
    const [clinicData, setClinicData] = useState([]);

    // Debugging logs
    console.log("ðŸ“Œ allLocations received in Analystics.js:", allLocations);

    // Fetch clinic attributes from API
    useEffect(() => {
        const fetchAttributes = async () => {
            if (allLocations.length > 0) {
                try {
                    const response = await axios.get("http://127.0.0.1:5000/search", {
                        params: { city: "KÃ¶ln", icd_code: "D27" } // Temporary fix
                    });

                    console.log("âœ… API Response in Analystics.js:", response.data);

                    if (response.data.length > 0) {
                        setClinicData(response.data);
                    } else {
                        console.warn("âš ï¸ No clinic data received.");
                    }
                } catch (error) {
                    console.error("âŒ API Error fetching clinic data:", error);
                }
            }
        };

        fetchAttributes();
    }, [allLocations]);

    // Fetch road distances between the user's location and all other clinics
    useEffect(() => {
        const fetchDistances = async () => {
            if (!allLocations.length) return;

            const newDistances = {};
            for (const location of allLocations) {
                if (!location.latitude || !location.longitude) continue;

                const roadDistance = await getRoadDistance(
                    location.latitude, location.longitude,
                    location.latitude, location.longitude
                );

                newDistances[location.clinic_name] = roadDistance;
            }

            setDistances(newDistances);
        };

        fetchDistances();
    }, [allLocations]);

    return (
        <>
            {/* Informations Box */}
            <div style={styles.informationsContainer}>
                <h3 style={styles.title}>Informations</h3>
                <p>This section provides general insights based on selected locations.</p>
            </div>

            {/* Analystics Box */}
            <div style={styles.analysticContainer}>
                <h3 style={styles.title}>Analystic Data</h3>
                {clinicData.length > 0 ? (
                    <ul style={styles.list}>
                        {clinicData.map((clinic, index) => (
                            <li key={index} style={styles.listItem}>
                                <strong>Clinic:</strong> {clinic.clinic_name} ({clinic.clinic_identity || "N/A"})<br />
                                <strong>City:</strong> {clinic.city || "N/A"} <br />
                                <strong>Number of Beds:</strong> {clinic.number_of_beds || "N/A"} <br />
                                <strong>ICD Name:</strong> {clinic.icd_name || "N/A"} <br />
                                <strong>ICD Fallzahl:</strong> {clinic.icd_fallzahl || "N/A"} <br />
                                <strong>Road Distance:</strong> <span style={styles.bold}>{distances[clinic.clinic_name] || "Calculating..."} km</span>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p style={styles.noData}>No data available.</p>
                )}
            </div>
        </>
    );
};

// CSS-in-JS styles
