    informationsContainer: {
        width: "35%",
        fontWeight: "600",
        position: "absolute",
        right: 0,
        top: "100px", // Position above Analystics
        padding: "20px",
        backgroundColor: "#e3f2fd",
        boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
        borderRadius: "8px",
