        listStyleType: "none",
        padding: 0
    },
    listItem: {
        marginBottom: "10px",
        padding: "10px",
        borderBottom: "1px solid #ddd"
    },
    bold: {
        fontWeight: "600"
    },
    noData: {
        fontStyle: "italic",
        color: "#999"
    }
};

export default Analystics;
