import React, { useState, useEffect, useMemo } from "react";
import { MapContainer, TileLayer, useMap, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import SearchForm from "./SearchForm";
import L from "leaflet";
import { useLocation } from "./LocationContext";
import Button from '@mui/material/Button';

// 🎯 Predefined colors for lines/markers
const lineColors = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

// 🟢 User icon for geolocation marker
const userIcon = L.icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/684/684908.png",
    iconSize: [35, 35],
    iconAnchor: [17, 35]
});

// 🏥 Create a circular icon for clinics/MVZ/etc.
const getColoredClinicIcon = (color) => {
    return L.divIcon({
        className: "custom-clinic-marker",
        html: `<div style="
            width: 30px; height: 30px; 
            background-color: ${color}; 
            border-radius: 50%; 
            border: 1px solid black;
            display: flex; justify-content: center; align-items: center;">
        </div>`,
        iconSize: [20, 20],
        iconAnchor: [15, 20]
    });
};

// ✅ AutoZoom adjusts the map bounds to show all valid points
const AutoZoom = ({ locations }) => {
    const map = useMap();
    
    useEffect(() => {
        const validLocations = locations.filter(loc => 
            (loc.latitude && loc.longitude) ||
            (loc.clinic_latitude && loc.clinic_longitude) ||
            (loc.mvz_latitude && loc.mvz_longitude) ||
            (loc.asv_latitude && loc.asv_longitude) ||
            (loc.niedergelassene_latitude && loc.niedergelassene_longitude) ||
            (loc.neurologe_latitude && loc.neurologe_longitude) ||
            (loc.onkologe_latitude && loc.onkologe_longitude)
        );

        if (validLocations.length > 0) {
            const bounds = L.latLngBounds(validLocations.map(loc => [
                loc.latitude || loc.clinic_latitude || loc.mvz_latitude || loc.asv_latitude || loc.niedergelassene_latitude || loc.neurologe_latitude || loc.onkologe_latitude,
                loc.longitude || loc.clinic_longitude || loc.mvz_longitude || loc.asv_longitude || loc.niedergelassene_longitude || loc.neurologe_longitude || loc.onkologe_longitude
            ]));
            map.fitBounds(bounds, { padding: [50, 50] });
        } else {
            console.warn("⚠️ No valid locations found for AutoZoom!");
        }
    }, [locations, map]);

    return null;
};

const Map = () => {
    const [graphData, setGraphData] = useState([]);             // Holds clinic/MVZ/etc. data
    const [userLocation, setUserLocation] = useState(null);     // User's position
    const [locationChoice, setLocationChoice] = useState("");   // Selected location method
    const [manualAddress, setManualAddress] = useState("");     // For manual input
    const [selectedLocation, setSelectedLocation] = useState(null); // 🆕 Selected clicked location
    const { setAllLocations } = useLocation();                  // Context function

    // 🧭 Get user's current location
    const fetchMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;

                    if (lat && lon) {
                        setUserLocation({ latitude: lat, longitude: lon });
                        console.log("✅ User location set:", lat, lon);
                    }
                },
                (error) => {
                    console.error("❌ Error getting location:", error);
                }
            );
        }
    };

    // 📍 Handle click on marker → save clicked location
    const handleMarkerClick = (location) => {
        setSelectedLocation(location); // 🆕 Sets the selected location on click
    };

    // 📍 Handle location input choice (manual or geolocation)
    const handleLocationChoice = (choice) => {
        setLocationChoice(choice);
        setUserLocation(null);
        setManualAddress("");
        if (choice === "my-location") {
            fetchMyLocation();
        }
    };

    // 📬 Convert manual address to lat/lng via OpenStreetMap API
    const handleManualAddressSearch = async () => {
        if (!manualAddress.trim()) {
            alert("Please enter a valid address.");
            return;
        }
        try {
            const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(manualAddress)}`);
            const data = await response.json();
            if (data.length > 0 && data[0].lat && data[0].lon) {
                setUserLocation({
                    latitude: parseFloat(data[0].lat),
                    longitude: parseFloat(data[0].lon)
                });
            } else {
                alert("Address not found.");
            }
        } catch (error) {
            console.error("Error fetching location:", error);
            alert("Failed to fetch location.");
        }
    };

    // 📍 Combine user and graph data for the map
    const allLocations = useMemo(() => {
        const locations = [...graphData];
        if (userLocation) {
            locations.push({ clinic_name: "Your Location", provider_type: "User", ...userLocation });
        }
        return locations;
    }, [graphData, userLocation]);

    // 🔁 Update locations globally
    useEffect(() => {
        setAllLocations(allLocations);
    }, [allLocations, setAllLocations]);

    return (
        <div style={{ padding: "10px", backgroundColor: "#f0ffff", borderRadius: "10px" }}>
            <h3>Wählen Sie Ihren Standort:</h3>
            <div style={{ display: "flex", gap: "10px", marginBottom: "10px" }}>
                <Button variant="contained" onClick={() => handleLocationChoice("my-location")}>
                    Meinen Standort verwenden
                </Button>
                <Button variant="contained" onClick={() => handleLocationChoice("manual")}>
                    Standort manuell eingeben
                </Button>
            </div>

            {/* 📝 Input for manual address */}
            {locationChoice === "manual" && (
                <div style={{ marginBottom: "10px" }}>
                    <input
                        type="text"
                        value={manualAddress}
                        onChange={(e) => setManualAddress(e.target.value)}
                        placeholder="Geben Sie Ihre Adresse ein"
                        style={{ padding: "8px", width: "250px", marginRight: "10px" }}
                    />
                    <button onClick={handleManualAddressSearch} style={{ padding: "8px 12px" }}>
                        Search Address
                    </button>
                </div>
            )}

            {/* 🔍 Search component */}
            <SearchForm setGraphData={setGraphData} />

            {/* 🗺️ Map section */}
            <div style={{ height: "500px", width: "100%", marginTop: "15px" }}>
                <MapContainer center={[50.851, 8.015]} zoom={8} scrollWheelZoom={false} style={{ height: "100%", width: "100%" }}>
                    <TileLayer
                        attribution='&copy; OpenStreetMap contributors'
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    />

                    {/* 🔍 Auto-zoom to valid locations */}
                    <AutoZoom locations={allLocations} />

                    {/* 🟢 Marker for user location */}
                    {userLocation && (
                        <Marker
                            position={[userLocation.latitude, userLocation.longitude]}
                            icon={userIcon}
                            eventHandlers={{ click: () => handleMarkerClick(userLocation) }}
                        >
                            <Popup><strong>Your Location</strong></Popup>
                        </Marker>
                    )}

                    {/* 🏥 Marker for each clinic/MVZ/etc. */}
                    {graphData.map((data, index) => {
                        const color = lineColors[index % lineColors.length];
                        const latitude = data.clinic_latitude || data.mvz_latitude || data.neurologe_latitude || data.onkologe_latitude;
                        const longitude = data.clinic_longitude || data.mvz_longitude || data.neurologe_longitude || data.onkologe_longitude;
                        if (!latitude || !longitude) return null;

                        return (
                            <Marker
                                key={index}
                                position={[latitude, longitude]}
                                icon={getColoredClinicIcon(color)}
                                eventHandlers={{ click: () => handleMarkerClick(data) }} // 🆕 Click handler
                            >
                                <Popup>
                                    <strong>{data.clinic_name || data.mvz_name || data.neurologe_name || data.onkologe_name}</strong><br />
                                    City: {data.clinic_city || data.mvz_city || data.neurologe_city || data.onkologe_city} <br />
                                    Address: {data.clinic_address || data.mvz_address || data.neurologe_address || data.onkologe_address} <br />
                                    Beds: {data.clinic_number_of_beds || "N/A"}
                                </Popup>
                            </Marker>
                        );
                    })}

                    {/* 🔴 Draw lines from user to each location */}
                    {userLocation && graphData.map((data, index) => {
                        const color = lineColors[index % lineColors.length];
                        const lat = data.clinic_latitude || data.mvz_latitude || data.neurologe_latitude || data.onkologe_latitude;
                        const lon = data.clinic_longitude || data.mvz_longitude || data.neurologe_longitude || data.onkologe_longitude;
                        if (!lat || !lon) return null;

                        return (
                            <Polyline
                                key={index}
                                positions={[
                                    [userLocation.latitude, userLocation.longitude],
                                    [lat, lon]
                                ]}
                                color={color}
                                weight={2}
                            />
                        );
                    })}
                </MapContainer>
            </div>

            {/* ℹ️ Show selected location info below the map */}
            {selectedLocation && (
                <div style={{ marginTop: "15px", backgroundColor: "#fff", padding: "10px", borderRadius: "8px", border: "1px solid #ccc" }}>
                    <h4>Ausgewählte Einrichtung:</h4>
                    <p><strong>Name:</strong> {selectedLocation.clinic_name || selectedLocation.mvz_name || selectedLocation.neurologe_name || selectedLocation.onkologe_name}</p>
                    <p><strong>Adresse:</strong> {selectedLocation.clinic_address || selectedLocation.mvz_address || selectedLocation.neurologe_address || selectedLocation.onkologe_address}</p>
                    <p><strong>Stadt:</strong> {selectedLocation.clinic_city || selectedLocation.mvz_city || selectedLocation.neurologe_city || selectedLocation.onkologe_city}</p>
                </div>
            )}
        </div>
    );
};

export default Map;
