import React, { useState, useEffect } from "react";
import SearchForm from "./SearchForm";

const GraphView = () => {
  const [graphData, setGraphData] = useState([]);

  useEffect(() => {
    console.log("Updated Graph Data:", graphData); // ✅ Debugging

    if (graphData.length > 0) {
      // Assuming GraphXR accepts updates via URL parameters
      const newURL = `https://graphxr.kineviz.com/p/67927d2d878e960e37541211/New-Ways-of-Care-Provision-and-Analysis-of-Patient-Flows?data=${encodeURIComponent(
        JSON.stringify(graphData)
      )}`;

      document.getElementById("graphxr-iframe").src = newURL;
    }
  }, [graphData]); // Runs when graphData changes

  return (
    <div>
      <h1>Healthcare Data Visualization with GraphXR</h1>
      <SearchForm setGraphData={setGraphData} />
      <iframe
        id="graphxr-iframe"
        src="https://graphxr.kineviz.com/p/67927d2d878e960e37541211/New-Ways-of-Care-Provision-and-Analysis-of-Patient-Flows"
        width="100%"
        height="600px"
        title="GraphXR Visualization"
      ></iframe>
    </div>
  );
};

export default GraphView;
