import { useState } from "react";
import axios from "axios";

const SearchForm = ({ setGraphData }) => {
    // State for search fields
    const [city, setCity] = useState("");
    const [icdCode, setIcdCode] = useState("");
    const [radius, setRadius] = useState(30); // Default 30 km

    // State for checkboxes (filters)
    const [focus, setFocus] = useState({
        onkologen: false,
        neurologen: false,
    });
    const [providers, setProviders] = useState({
        clinics: false,
        mvz: false,
        asv: false,
        niedergelasseneAertze: false,
    });
    const [cooperation, setCooperation] = useState(false);

    // const handleSearch = async (e) => {
    //     e.preventDefault();
    //     console.log("üîµ Search Button Clicked");
    
    //     try {
    //         console.log(`üîµ Sending request to: http://127.0.0.1:5000/search?city=${city}&icd_code=${icdCode}`);
            
    //         const response = await axios.get("http://127.0.0.1:5000/search", {
    //             params: { city, icd_code: icdCode, radius, focus, providers, cooperation },
    //         });
    
    //         console.log("‚úÖ API Response Received:", response.data);
    
    //         if (response.data.length > 0) {
    //             setGraphData(response.data);  // üî¥ Ensure GraphData is updated
    //         } else {
    //             console.warn("‚ö†Ô∏è No data returned from API");
    //         }
    //     } catch (error) {
    //         console.error("‚ùå API Error:", error);
    //     }
    const handleSearch = async (e) => {
        e.preventDefault();
        console.log("üîµ Search Button Clicked");
    
        try {
            console.log(`üîµ Sending request to: http://127.0.0.1:5000/search`);
    
            const response = await axios.get("http://127.0.0.1:5000/search", {
                params: {
                    city,
                    icd_code: icdCode,
                    radius,
                    onkologen: focus.onkologen,
                    neurologen: focus.neurologen,
                    clinic: providers.clinics,
                    mvz: providers.mvz,
                    asv: providers.asv,
                    cooperation
                }
            });
    
            console.log("‚úÖ API Response Received:", response.data);
    
            if (response.data.length > 0) {
                setGraphData(response.data);  // ‚úÖ Update state with API response
            } else {
                console.warn("‚ö†Ô∏è No data returned from API");
                setGraphData([]); // Ensure the map clears when no data is returned
            }
        } catch (error) {
            console.error("‚ùå API Error:", error);
        }
    
    

    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleSearch} style={styles.form}>
                {/* üü¢ FLEX CONTAINER FOR SEARCH FIELDS & GUIDELINE BOX */}
                <div style={styles.searchRow}>
                    
                    {/* üü¢ SEARCH FIELDS (CITY & ICD CODE) */}
                    <div style={styles.inputContainer}>
                        <input
                            type="text"
                            placeholder="City"
                            value={city}
                            onChange={(e) => setCity(e.target.value)}
                            style={styles.input}
                            required= {true}
                        />
                        <input
                            type="text"
                            placeholder="ICD Code"
                            value={icdCode}
                            onChange={(e) => setIcdCode(e.target.value)}
                            style={styles.input}
                        />
                    </div>
                    
                    {/* üü¢ GUIDELINE BOX */}
                    <div style={styles.guideBox}>
                        <strong>Sucheingaben: Diagnosen, Stadt oder eine der folgenden Optionen</strong>
                    </div>
                </div>

                {/* üü¢ RADIUS INPUT */}
                <div style={styles.radiusContainer}>
                    <label>Umkreis  </label>
                    <input
                        type="number"
                        value={radius}
                        onChange={(e) => setRadius(e.target.value)}
                        style={styles.radiusInput}
                    />
                    <span>km</span>
                </div>

                {/* üü¢ FILTERS (Schwerpunkt, Leistungserbringer & Kooperationen in the same row) */}
                <div style={styles.filterRow}>
                    
                    {/* üü¢ SCHWERPUNKT FILTER */}
                    <div style={styles.filterBox}>
                        <h4>Schwerpunkt</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={focus.oncology}
                                onChange={() => setFocus({ ...focus, oncology: !focus.oncology })}
                            />
                            Onkologie
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={focus.neurology}
                                onChange={() => setFocus({ ...focus, neurology: !focus.neurology })}
                            />
                            Neurologie
                        </label>
                    </div>

                    {/* üü¢ LEISTUNGSERBRINGER FILTER (Same Line) */}
                    <div style={styles.filterBox}>
                        <h4>Leistungserbringer</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.clinics}
                                onChange={() => setProviders({ ...providers, clinics: !providers.clinics })}
                            />
                            Kliniken
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.mvz}
                                onChange={() => setProviders({ ...providers, mvz: !providers.mvz })}
                            />
                            MVZ
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.asv}
                                onChange={() => setProviders({ ...providers, asv: !providers.asv })}
                            />
                            ASV
                        </label>
                        <label>
                            <input
                                type="checkbox"
                                checked={providers.niedergelasseneAertze}
                                onChange={() => setProviders({ ...providers, niedergelasseneAertze: !providers.niedergelasseneAertze })}
                            />
                            Niedergelassene √Ñrzte
                        </label>
                    </div>

                    {/* üü¢ KOOPERATIONEN TOGGLE (Same Row) */}
                    <div style={styles.filterBox}>
                        <h4>Kooperationen</h4>
                        <label>
                            <input
                                type="checkbox"
                                checked={cooperation}
                                onChange={() => setCooperation(!cooperation)}
                            />
                            Kooperationen anzeigen
                        </label>
                    </div>

                </div>

                {/* üü¢ SEARCH BUTTON */}
                <button type="submit" style={styles.searchButton}>Suche</button>
            </form>
        </div>
    );
};

// üü¢ CSS STYLES
const styles = {
    container: {
        padding: "10px",
        borderRadius: "8px",
        backgroundColor: "#f9f9f9",
        width: "59%",
        height: "auto",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
        boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.1)",
        overflow: "auto",
        marginBottom: "5px",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "10px",
        width: "100%",
    },
    searchRow: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        width: "100%",
    },
    inputContainer: {
        display: "flex",
        gap: "10px",
        alignItems: "center",
        flex: "2",
    },
    input: {
        fontSize: "12px",
        padding: "5px",
        width: "100%",
    },
    guideBox: {
        flex: "3",
        backgroundColor: "#e3f2fd",
        justifyContent: "space-between",
        padding: "5px",
        borderRadius: "5px",
        fontSize: "12px",
        boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.1)",
    },
    filterRow: {
        display: "flex",
        justifyContent: "space-between",
        alignItems: "flex-start",
        gap: "20px",
    },
    filterBox: {
        flex: 1,
        backgroundColor: "#fff",
        padding: "10px",
        borderRadius: "5px",
        boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.1)",
        fontSize: "12px",
        width: "30%", // Adjusted for three columns
    },
    searchButton: {
        padding: "10px",
        backgroundColor: "#007BFF",
        color: "#fff",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
        width: "10%",
        fontSize: "12px",
    },
};

export default SearchForm;
