import React, { useState, useEffect } from "react";
import { useLocation } from "./LocationContext";


const getAttribute = async (ID=[]) => { 

  try {
    console.log(`ðŸ”µ Sending request to: http://127.0.0.1:5000/search?city=${city}&icd_code=${icdCode}`);
    
    const response = await axios.get("http://127.0.0.1:5000/search", {
        params: { ID: ID},
    });

    console.log("âœ… API Response Received:", response.data);
    // setGraphData(response.data);
} catch (error) {
    console.error("âŒ API Error:", error);
}

};

// Function to get road distance from OSRM API
const getRoadDistance = async (lat1, lon1, lat2, lon2) => {
  const url = `https://router.project-osrm.org/route/v1/driving/${lon1},${lat1};${lon2},${lat2}?overview=false`;
  
  try {
      const response = await fetch(url);
      const data = await response.json();
      if (data.routes && data.routes.length > 0) {
          const distanceInKm = (data.routes[0].distance / 1000).toFixed(2); // Convert meters to km
          return distanceInKm;
      } else {
          return "N/A";
      }
  } catch (error) {
      console.error("Error fetching road distance:", error);
      return "N/A";
  }
};

const Analystics = () => {
  const { allLocations } = useLocation();
  const [distances, setDistances] = useState({});
  const [attr, setattr] = useState({});

  // Find the user's location (if available)
  const userLocation = allLocations.find(loc => loc.clinic_name === "Your Location");

  useEffect(() => {
    // Fetch the attribute data for all clinics
    if (allLocations.length > 0) {
        const IDs = allLocations.map(loc => loc.clinic_id);
       setattr (getAttribute(IDs));
        console.log("ï¿½ï¿½ Attribute Data Received:", attr);

    }

  }, [allLocations]);

  // Fetch the distance between the user's location and all other clinics
  useEffect(() => {
      const fetchDistances = async () => {
          if (!userLocation) return;

          const newDistances = {};
          for (const location of allLocations) {
              if (location.clinic_name === "Your Location") continue;

              const roadDistance = await getRoadDistance(
                  userLocation.latitude, userLocation.longitude,
                  location.latitude, location.longitude
              );

              newDistances[location.clinic_name] = roadDistance;
          }

          setDistances(newDistances);
      };

      fetchDistances();
  }, [userLocation, allLocations]);

  return (
      <div>
          <h3>Analystic Data</h3>
          {allLocations.length > 0 ? (
              <ul>
                  {allLocations.map((location, index) => {
                      if (location.clinic_name === "Your Location") return null;

                      return (
                          <li key={index}>
                              {location.clinic_name} - Latitude: {location.latitude}, Longitude: {location.longitude}
                              <br />
                              Road Distance: {distances[location.clinic_name] || "Calculating..."} km
                          </li>
                      );
                  })}
              </ul>
          ) : (
              <p>No data available.</p>
          )}
      </div>
  );
};

export default Analystics;
