import { useState } from "react";
import axios from "axios";

const SearchForm = ({ setGraphData }) => {
    const [city, setCity] = useState("");
    const [icdCode, setIcdCode] = useState("");
    const [radius, setRadius] = useState(30); // Default 30 km
    const [focus, setFocus] = useState({
        oncology: true,
        neurology: false,
    });
    const [providers, setProviders] = useState({
        clinics: true,
        mvz: true,
        asv: true,
        niedergelasseneAertze: false,
    });
    const [cooperation, setCooperation] = useState(false);

    const handleSearch = async (e) => {
        e.preventDefault();
        console.log("üîµ Search Button Clicked");

        try {
            console.log(`üîµ Sending request to: http://127.0.0.1:5000/search`);
            
            const response = await axios.get("http://127.0.0.1:5000/search", {
                params: {
                    city,
                    icd_code: icdCode,
                    radius,
                    focus: Object.keys(focus).filter((key) => focus[key]), // Get selected focus
                    providers: Object.keys(providers).filter((key) => providers[key]), // Get selected providers
                    cooperation,
                },
            });

            console.log("‚úÖ API Response Received:", response.data);
            setGraphData(response.data);
        } catch (error) {
            console.error("‚ùå API Error:", error);
        }
    };

    return (
        <div style={styles.container}>
            <form onSubmit={handleSearch} style={styles.form}>
                <input
                    type="text"
                    placeholder="City"
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="ICD Code"
                    value={icdCode}
                    onChange={(e) => setIcdCode(e.target.value)}
                />
                <div style={styles.radiusContainer}>
                    <label>Umkreis:</label>
                    <input
                        type="number"
                        value={radius}
                        onChange={(e) => setRadius(e.target.value)}
                        style={styles.radiusInput}
                    />
                    <span>km</span>
                </div>

                {/* Schwerpunkt (Focus) */}
                <div style={styles.filterSection}>
                    <h4>Schwerpunkt</h4>
                    <label>
                        <input
                            type="checkbox"
                            checked={focus.oncology}
                            onChange={() => setFocus({ ...focus, oncology: !focus.oncology })}
                        />
                        Onkologie
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            checked={focus.neurology}
                            onChange={() => setFocus({ ...focus, neurology: !focus.neurology })}
                        />
                        Neurologie
                    </label>
                </div>

                {/* Leistungserbringer (Providers) */}
                <div style={styles.filterSection}>
                    <h4>Leistungserbringer</h4>
                    <label>
                        <input
                            type="checkbox"
                            checked={providers.clinics}
                            onChange={() => setProviders({ ...providers, clinics: !providers.clinics })}
                        />
                        Kliniken
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            checked={providers.mvz}
                            onChange={() => setProviders({ ...providers, mvz: !providers.mvz })}
                        />
                        MVZ
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            checked={providers.asv}
                            onChange={() => setProviders({ ...providers, asv: !providers.asv })}
                        />
                        ASV
                    </label>
                    <label>
                        <input
                            type="checkbox"
                            checked={providers.niedergelasseneAertze}
                            onChange={() => setProviders({ ...providers, niedergelasseneAertze: !providers.niedergelasseneAertze })}
                        />
                        Niedergelassene √Ñrzte
                    </label>
                </div>

                {/* Kooperationen Toggle */}
                <div style={styles.toggleContainer}>
                    <label>Kooperationen</label>
                    <input
                        type="checkbox"
                        checked={cooperation}
                        onChange={() => setCooperation(!cooperation)}
                        style={styles.toggleSwitch}
                    />
                </div>

                <button type="submit" style={styles.searchButton}>Search</button>
            </form>
        </div>
    );
};

// Styles for the search form
const styles = {
    container: {
        padding: "10px",
        borderRadius: "8px",
        backgroundColor: "#f9f9f9",
        width: "100%",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "10px",
    },
    radiusContainer: {
        display: "flex",
        alignItems: "center",
        gap: "5px",
    },
    radiusInput: {
        width: "50px",
    },
    filterSection: {
        display: "flex",
        flexDirection: "column",
        backgroundColor: "#fff",
        padding: "10px",
        borderRadius: "5px",
        boxShadow: "0px 0px 5px rgba(0, 0, 0, 0.1)",
    },
    toggleContainer: {
        display: "flex",
        alignItems: "center",
        gap: "10px",
        marginTop: "10px",
    },
    toggleSwitch: {
        width: "20px",
        height: "20px",
        cursor: "pointer",
    },
    searchButton: {
        padding: "10px",
        backgroundColor: "#007BFF",
        color: "#fff",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
    },
};

export default SearchForm;
