import React, { useState, useEffect, useMemo } from "react";
import { MapContainer, TileLayer, useMap, Marker, Popup, Polyline } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import SearchForm from "./SearchForm";
import L from "leaflet";
import { useLocation } from "./LocationContext";

// 🎨 Dynamic Marker Icons based on Provider Type and Bed Count
const getCustomIcon = (providerType, bedCount) => {
    let iconSize = bedCount ? Math.min(50, Math.max(20, bedCount / 15)) : 25; // Adjust icon size
    let iconUrl = "";

    switch (providerType) {
        case "Clinic":
            iconUrl = "https://cdn-icons-png.flaticon.com/512/2874/2874783.png"; // Hospital Icon
            break;
        case "MVZ":
            iconUrl = "https://cdn-icons-png.flaticon.com/512/3062/3062634.png"; // Medical Practice Icon
            iconSize = 25; // Fixed small size
            break;
        case "ASV":
            iconUrl = "https://cdn-icons-png.flaticon.com/512/1048/1048953.png"; // ASV Icon
            iconSize = 25; // Fixed small size
            break;
        case "User":
            iconUrl = "https://cdn-icons-png.flaticon.com/512/684/684908.png"; // User Location Icon
            iconSize = 30;
            break;
        default:
            iconUrl = "https://cdn-icons-png.flaticon.com/512/684/684908.png"; // Default location pin
            iconSize = 25;
            break;
    }

    return new L.Icon({
        iconUrl,
        iconSize: [iconSize, iconSize],
        iconAnchor: [iconSize / 2, iconSize],
    });
};

// 🎨 Predefined colors for lines
const lineColors = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

// 🎯 Auto-zoom Component
const AutoZoom = ({ locations }) => {
    const map = useMap();
    useEffect(() => {
        if (locations.length > 0) {
            const bounds = L.latLngBounds(locations.map(loc => [loc.latitude, loc.longitude]));
            map.fitBounds(bounds, { padding: [50, 50] });
        }
    }, [locations, map]);
    return null;
};

const Map = () => {
    const [graphData, setGraphData] = useState([]);
    const [userLocation, setUserLocation] = useState(null);
    const [locationChoice, setLocationChoice] = useState(""); // "my-location" or "manual"
    const [manualAddress, setManualAddress] = useState(""); // User's address input
    const { setAllLocations } = useLocation();

    // 📍 Fetch user's current location
    const fetchMyLocation = () => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    setUserLocation({
                        latitude: position.coords.latitude,
                        longitude: position.coords.longitude
                    });
                },
                (error) => {
                    console.error("Error getting location:", error);
                }
            );
        }
    };

    // 📍 Handle location selection
    const handleLocationChoice = (choice) => {
        setLocationChoice(choice);
        setUserLocation(null);
        setManualAddress("");
        if (choice === "my-location") {
            fetchMyLocation();
        }
    };

    // 📍 Convert address to lat/lon using OpenStreetMap's API
    const handleManualAddressSearch = async () => {
        if (!manualAddress.trim()) {
            alert("Please enter a valid address.");
            return;
        }
        try {
            const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(manualAddress)}`);
            const data = await response.json();
            if (data.length > 0) {
                setUserLocation({
                    latitude: parseFloat(data[0].lat),
                    longitude: parseFloat(data[0].lon)
                });
            } else {
                alert("Address not found. Please enter a more precise location.");
            }
        } catch (error) {
            console.error("Error fetching geolocation:", error);
            alert("Failed to fetch location. Try again later.");
        }
    };

    // 📌 Memoize locations
    const allLocations = useMemo(() => {
        const locations = [...graphData];
        if (userLocation) {
            locations.push({ clinic_name: "Your Location", provider_type: "User", ...userLocation });
        }
        return locations;
    }, [graphData, userLocation]);

    // ✅ Update locations in the context
    useEffect(() => {
        setAllLocations(allLocations);
    }, [allLocations, setAllLocations]);

    return (
        <div>
            <h1>New Ways of Care Provision and Analysis of Patient Flows</h1>

            {/* 🌍 Location Selection */}
            <div>
                <h3>Select Your Location:</h3>
                <button onClick={() => handleLocationChoice("my-location")}>Use My Location</button>
                <button onClick={() => handleLocationChoice("manual")}>Enter Location Manually</button>
            </div>

            {/* 📌 Manual Address Input */}
            {locationChoice === "manual" && (
                <div>
                    <input 
                        type="text" 
                        placeholder="Enter your address" 
                        value={manualAddress} 
                        onChange={(e) => setManualAddress(e.target.value)}
                    />
                    <button onClick={handleManualAddressSearch}>Search Address</button>
                </div>
            )}

            {/* 🔎 Search Form */}
            <SearchForm setGraphData={setGraphData} />

            {/* 🗺️ Map Display */}
            <MapContainer center={[50.851, 8.015]} zoom={8} scrollWheelZoom={false} style={{ height: 500, width: "60%" }}>
                <TileLayer
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                />
                {/* Auto-Zoom Feature */}
                <AutoZoom locations={allLocations} />

                {/* 📍 Add Clinic & Provider Markers */}
                {graphData.map((data, index) => (
                    <Marker 
                        key={index} 
                        position={[data.latitude, data.longitude]} 
                        icon={getCustomIcon(data.provider_type, data.number_of_beds)}
                    >
                        <Popup>
                            <strong>{data.clinic_name}</strong><br />
                            City: {data.city} <br />
                            {data.number_of_beds ? `Beds: ${data.number_of_beds}` : "MVZ / ASV"}<br />
                        </Popup>
                    </Marker>
                ))}

                {/* 📍 User's Location Marker */}
                {userLocation && (
                    <Marker position={[userLocation.latitude, userLocation.longitude]} icon={getCustomIcon("User", null)}>
                        <Popup>
                            <strong>Your Location</strong><br />
                            Lat: {userLocation.latitude}, Lng: {userLocation.longitude}
                        </Popup>
                    </Marker>
                )}

                {/* 🛣️ Draw Lines */}
                {userLocation && graphData.map((data, index) => (
                    <Polyline 
                        key={index} 
                        positions={[[userLocation.latitude, userLocation.longitude], [data.latitude, data.longitude]]} 
                        color={lineColors[index % lineColors.length]} 
                        weight={3}
                    />
                ))}
            </MapContainer>
        </div>
    );
};

export default Map;
