import React from "react";
import { LocationProvider } from "./components/LocationContext";
import Analystics from "./components/Analystics";
import Map from "./components/map"; 

function App() {
  return (
  //   <div>
  //     <div style={{
  //       display: "flex", 
  //       alignItems: "center",
  //       justifyContent: "space-between", 
  //       backgroundColor: "#f9f9f9",
  //       border: "2px solid #b3e5fc",
  //       borderRadius: "10px",
  //       padding: "10px",
  //       textAlign: "center",
  //       boxShadow: "2px 2px 5px rgba(0,0,0,0.1)",
  //       margin: "15px auto",  // Center horizontally while reducing space
  //       width: "100%",  // Make it take up more width
  //       maxWidth: "1450px"  // Limit maximum width for responsiveness
  //     }}>
        
  //       {/* Left Logo */}
  //       <img src="/Images/UNIS-f5.jpg" alt="UNIS-f5" style={{ height: "50px", width: "75px" }} />



  //     {/* Title */}
  //     <h1 style={{ fontSize: "24px", fontWeight: "bold", margin: "0", flexGrow: 1, textAlign: "center" }}>
  //         Herzlich Willkommen zu neuen Wegen der Versorgung durch Analyse von Patientenströmen!
  //     </h1>

  //     {/* Right Logo */}
  //     <img src="/Images/UNIS.jpg" alt="UNIS" style={{ height: "50px", width: "75px" }} />

  // </div>
  <div style={{
    backgroundImage: "url('/Images/back.jpg')", // ✅ Path to the background image
    backgroundSize: "cover", // ✅ Cover the entire screen
    backgroundPosition: "center", // ✅ Center the background
    backgroundRepeat: "no-repeat", // ✅ Prevent repetition
    minHeight: "100vh", // ✅ Ensure full viewport height
    width: "100%", // ✅ Full width
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px", // ✅ Add some padding
  }}>
    
    {/* Header Section */}
    <div style={{
      display: "flex", 
      alignItems: "center",
      justifyContent: "space-between", 
      backgroundColor: "rgba(255, 255, 255, 0.9)", // ✅ Slight transparency
      border: "2px solid #b3e5fc",
      borderRadius: "10px",
      padding: "10px",
      textAlign: "center",
      boxShadow: "2px 2px 5px rgba(0,0,0,0.1)",
      margin: "15px auto",  
      width: "100%",  
      maxWidth: "1450px",
    }}>

      {/* Left Logo */}
      <img src="/Images/UNIS-f5.jpg" alt="UNIS-f5" style={{ height: "50px", width: "75px" }} />

      {/* Title */}
      <h1 style={{ fontSize: "24px", fontWeight: "bold", margin: "0", flexGrow: 1, textAlign: "center" }}>
        Herzlich Willkommen zu neuen Wegen der Versorgung durch Analyse von Patientenströmen!
      </h1>

      {/* Right Logo */}
      <img src="/Images/UNIS.jpg" alt="UNIS" style={{ height: "50px", width: "75px" }} />

    </div>
      <LocationProvider>
        <Map />
        <Analystics />
      </LocationProvider>
    </div>
  );
}


export default App;
