import { useState } from "react";
import axios from "axios";

const SearchForm = ({ setGraphData }) => {
    const [city, setCity] = useState("");
    const [address, setAddress] = useState("");
    const [useMyLocation, setUseMyLocation] = useState(false);
    const [radius, setRadius] = useState(30);

    const [focus, setFocus] = useState({
        onkologen: false,
        neurologen: false,
    });

    const [providers, setProviders] = useState({
        clinics: false,
        mvz: false,
        asv: false,
        niedergelasseneAertze: false,
    });

    const [cooperation, setCooperation] = useState(false);
    const [icdCode, setIcdCode] = useState("");

    // Handle ICD/Specialty exclusive logic
    const handleIcdCodeChange = (e) => {
        const value = e.target.value;
        setIcdCode(value);
        if (value !== "") {
            setFocus({ onkologen: false, neurologen: false });
        }
    };

    const handleFocusChange = (key) => {
        setFocus(prev => ({
            onkologen: key === "onkologen" ? !prev.onkologen : false,
            neurologen: key === "neurologen" ? !prev.neurologen : false
        }));
        if (icdCode !== "") setIcdCode(""); // Clear ICD if user toggles specialty
    };

    const handleSearch = async (e) => {
        e.preventDefault();
        console.log("üîµ Search Button Clicked");

        let locationParams = {};

        if (useMyLocation) {
            const position = await new Promise((resolve, reject) => {
                navigator.geolocation.getCurrentPosition(resolve, reject);
            });
            locationParams.lat = position.coords.latitude;
            locationParams.lon = position.coords.longitude;
        } else if (address) {
            locationParams.address = address;
        }

        try {
            const response = await axios.get("http://127.0.0.1:5000/search", {
                params: {
                    city,
                    icd_code: icdCode,
                    radius,
                    onkologen: focus.onkologen,
                    neurologen: focus.neurologen,
                    clinic: providers.clinics,
                    mvz: providers.mvz,
                    asv: providers.asv,
                    niedergelassene: providers.niedergelasseneAertze,
                    cooperation,
                    ...locationParams
                }
            });

            const validResults = response.data.filter(loc =>
                loc.latitude && loc.longitude
            );

            setGraphData(validResults);
        } catch (error) {
            console.error("‚ùå API Error:", error);
        }
    };

    return (
        <form onSubmit={handleSearch}>
            <input
                type="checkbox"
                checked={useMyLocation}
                onChange={() => {
                    setUseMyLocation(!useMyLocation);
                    if (!useMyLocation) setCity("");
                }}
            /> Use My Location

            <input
                type="text"
                placeholder="City"
                value={city}
                onChange={(e) => setCity(e.target.value)}
                disabled={useMyLocation || address}
            />

            <input
                type="text"
                placeholder="Address"
                value={address}
                onChange={(e) => setAddress(e.target.value)}
                disabled={useMyLocation || city}
            />

            <input
                type="text"
                placeholder="ICD Code"
                value={icdCode}
                onChange={handleIcdCodeChange}
            />

            <label>
                <input
                    type="checkbox"
                    checked={focus.onkologen}
                    onChange={() => handleFocusChange("onkologen")}
                    disabled={icdCode !== ""}
                />
                Onkologie
            </label>
            <label>
                <input
                    type="checkbox"
                    checked={focus.neurologen}
                    onChange={() => handleFocusChange("neurologen")}
                    disabled={icdCode !== ""}
                />
                Neurologie
            </label>

            <label>
                <input type="checkbox" checked={providers.clinics} onChange={() => setProviders(prev => ({ ...prev, clinics: !prev.clinics }))} />
                Kliniken
            </label>
            <label>
                <input type="checkbox" checked={providers.mvz} onChange={() => setProviders(prev => ({ ...prev, mvz: !prev.mvz }))} />
                MVZ
            </label>
            <label>
                <input type="checkbox" checked={providers.asv} onChange={() => setProviders(prev => ({ ...prev, asv: !prev.asv }))} />
                ASV
            </label>
            <label>
                <input type="checkbox" checked={providers.niedergelasseneAertze} onChange={() => setProviders(prev => ({ ...prev, niedergelasseneAertze: !prev.niedergelasseneAertze }))} />
                Niedergelassene √Ñrzte
            </label>
            <label>
                <input type="checkbox" checked={cooperation} onChange={() => setCooperation(!cooperation)} />
                Kooperationen anzeigen
            </label>

            <label>
                Radius:
                <input
                    type="number"
                    value={radius}
                    onChange={(e) => setRadius(e.target.value)}
                /> km
            </label>

            <button type="submit">Suche</button>
        </form>
    );
};

export default SearchForm;
