// export default Analystics;
import React, { useState, useEffect, useMemo } from "react";
import { useLocation } from "./LocationContext";

const lineColors = ["red", "blue", "green", "purple", "orange", "pink", "yellow"];

// üõ†Ô∏è Generate consistent keys for storing/retrieving distances
const getProviderKey = (type, name, address) => {
    const safeName = (name || "").trim();
    const safeAddress = (address || "").trim();
    return `${type}-${safeName}-${safeAddress}`;
};



// üöó Fetch road distance using OSRM API
const getRoadDistance = async (startLat, startLon, endLat, endLon) => {
    if (!startLat || !startLon || !endLat || !endLon) return "N/A";

    const url = `https://router.project-osrm.org/route/v1/driving/${startLon},${startLat};${endLon},${endLat}?overview=false`;

    try {
        const response = await fetch(url);
        const data = await response.json();
        if (data.routes?.length > 0) {
            return (data.routes[0].distance / 1000).toFixed(2); // meters ‚Üí km
        } else {
            console.warn("No route found", { startLat, startLon, endLat, endLon });
            return "N/A";
        }
    } catch (err) {
        console.error("Distance fetch error:", err);
        return "N/A";
    }
};

const Analystics = () => {
    const [distances, setDistances] = useState({});
    const { allLocations, selectedLocation } = useLocation();

    // ‚úÖ Detect origin location (user or city center)
    const originLocation = useMemo(() => {
        return allLocations.find(loc =>
            loc.clinic_name === "Your Location" || loc.clinic_name === "Zentrum (Umkreis)"
        );
    }, [allLocations]);
    

    // üöÄ Calculate distances once locations are ready
    useEffect(() => {
        const fetchDistances = async () => {
            if (!originLocation?.latitude || !originLocation?.longitude) {
                console.warn("üö´ Origin location missing!");
                return;
            }

            const newDistances = {};

            for (const provider of allLocations) {
                if (
                    provider.clinic_name === "Your Location" ||
                    provider.clinic_name === "Zentrum (Umkreis)"
                ) continue;

                const providerType = provider.clinic_name
                    ? "clinic"
                    : provider.mvz_name
                    ? "mvz"
                    : provider.asv_name
                    ? "asv"
                    : provider.niedergelassene_name
                    ? "niedergelassene"
                    : provider.neurologe_name
                    ? "neurologe"
                    : provider.onkologe_name
                    ? "onkologe"
                    : null;

                const providerName =
                    provider.clinic_name ||
                    provider.mvz_name ||
                    provider.asv_name ||
                    provider.niedergelassene_name ||
                    provider.neurologe_name ||
                    provider.onkologe_name;

                const providerAddress =
                    provider.clinic_address ||
                    provider.mvz_address ||
                    provider.asv_address ||
                    provider.niedergelassene_address ||
                    provider.neurologe_address ||
                    provider.onkologe_address;

                const lat =
                    provider.clinic_latitude ||
                    provider.mvz_latitude ||
                    provider.asv_latitude ||
                    provider.niedergelassene_latitude ||
                    provider.neurologe_latitude ||
                    provider.onkologe_latitude;

                const lon =
                    provider.clinic_longitude ||
                    provider.mvz_longitude ||
                    provider.asv_longitude ||
                    provider.niedergelassene_longitude ||
                    provider.neurologe_longitude ||
                    provider.onkologe_longitude;

                if (!providerType || !providerName || !lat || !lon) continue;

                const key = getProviderKey(providerType, providerName, providerAddress);
                const distance = await getRoadDistance(
                    originLocation.latitude,
                    originLocation.longitude,
                    lat,
                    lon
                );
                newDistances[key] = distance;
            }

            setDistances(newDistances);
        };

        fetchDistances();
    }, [originLocation, allLocations]);

    // üîç Determine selected item key
    const selectedProviderType = selectedLocation?.clinic_name
        ? "clinic"
        : selectedLocation?.mvz_name
        ? "mvz"
        : selectedLocation?.asv_name
        ? "asv"
        : selectedLocation?.niedergelassene_name
        ? "niedergelassene"
        : selectedLocation?.neurologe_name
        ? "neurologe"
        : selectedLocation?.onkologe_name
        ? "onkologe"
        : null;

    const selectedProviderName =
        selectedLocation?.clinic_name ||
        selectedLocation?.mvz_name ||
        selectedLocation?.asv_name ||
        selectedLocation?.niedergelassene_name ||
        selectedLocation?.neurologe_name ||
        selectedLocation?.onkologe_name;

    const selectedAddress =
        selectedLocation?.clinic_address ||
        selectedLocation?.mvz_address ||
        selectedLocation?.asv_address ||
        selectedLocation?.niedergelassene_address ||
        selectedLocation?.neurologe_address ||
        selectedLocation?.onkologe_address;

    const selectedDistanceKey = getProviderKey(
        selectedProviderType,
        selectedProviderName,
        selectedAddress
    );

    return (
        <>
            <div style={styles.informationsContainer}>
                <h3 style={styles.title}>Informationen zur ausgew√§hlten Einrichtung</h3>

                {selectedLocation ? (
                    <>
                        {selectedProviderName && (
                            <p><strong>Name:</strong> {selectedProviderName}</p>
                        )}
                        {selectedAddress && (
                            <p><strong>Adresse:</strong> {selectedAddress}</p>
                        )}
                        {(selectedLocation.clinic_city ||
                            selectedLocation.mvz_city ||
                            selectedLocation.asv_city ||
                            selectedLocation.niedergelassene_city ||
                            selectedLocation.neurologe_city ||
                            selectedLocation.onkologe_city) && (
                            <p><strong>Stadt:</strong> {selectedLocation.clinic_city ||
                                selectedLocation.mvz_city ||
                                selectedLocation.asv_city ||
                                selectedLocation.niedergelassene_city ||
                                selectedLocation.neurologe_city ||
                                selectedLocation.onkologe_city}</p>
                        )}
                        <p><strong>Beste Route:</strong>{" "}
                            <span style={styles.bold}>
                                {distances[selectedDistanceKey] || "Berechne..."} km
                            </span></p>
                    </>
                ) : (
                    <p style={{ fontStyle: "italic", color: "#666" }}>
                        Keine Einrichtung ausgew√§hlt.
                    </p>
                )}
            </div>

            <div style={styles.analysticContainer}>
                <h3 style={styles.title}>Leistungserbringer Informationen</h3>
                {allLocations.length > 0 ? (
                    <ul style={styles.list}>
                        {allLocations.map((provider, index) => {
                            if (
                                provider.clinic_name === "Your Location" ||
                                provider.clinic_name === "Zentrum (Umkreis)"
                            ) return null;

                            const type = provider.clinic_name
                                ? "clinic"
                                : provider.mvz_name
                                ? "mvz"
                                : provider.asv_name
                                ? "asv"
                                : provider.niedergelassene_name
                                ? "niedergelassene"
                                : provider.neurologe_name
                                ? "neurologe"
                                : provider.onkologe_name
                                ? "onkologe"
                                : null;

                            const name =
                                provider.clinic_name ||
                                provider.mvz_name ||
                                provider.asv_name ||
                                provider.niedergelassene_name ||
                                provider.neurologe_name ||
                                provider.onkologe_name;

                            const address =
                                provider.clinic_address ||
                                provider.mvz_address ||
                                provider.asv_address ||
                                provider.niedergelassene_address ||
                                provider.neurologe_address ||
                                provider.onkologe_address;

                            const key = getProviderKey(type, name, address);

                            if (!type || !name) return null;

                            return (
                                <li key={index} style={styles.listItem}>
                                    <span
                                        style={{
                                            backgroundColor: lineColors[index % lineColors.length],
                                            display: "inline-block",
                                            width: "12px",
                                            height: "12px",
                                            borderRadius: "50%",
                                            marginRight: "5px"
                                        }}
                                    />
                                    <strong>{name}</strong><br />
                                    {address && <><strong>Adresse:</strong> {address}<br /></>}
                                    {(provider.clinic_city || provider.mvz_city || provider.asv_city || provider.niedergelassene_city || provider.neurologe_city || provider.onkologe_city) && (
                                        <><strong>Stadt:</strong> {provider.clinic_city || provider.mvz_city || provider.asv_city || provider.niedergelassene_city || provider.neurologe_city || provider.onkologe_city}<br /></>
                                    )}
                                    <strong>Beste Route:</strong>{" "}
                                    <span style={styles.bold}>
                                        {distances[key] || "Berechne..."} km
                                    </span>
                                </li>
                            );
                        })}
                    </ul>
                ) : (
                    <p style={styles.noData}>Keine Daten verf√ºgbar.</p>
                )}
            </div>
        </>
    );
};

const styles = {
    informationsContainer: {
        width: "35%",
        fontWeight: "600",
        position: "absolute",
        border: "2px solid #b3e5fc",
        right: "10px",
        top: "130px",
        overflowY: "scroll",
        maxHeight: "200px",
        // bottom: "520px",
        padding: "15px",
        backgroundColor: "#e1ecf0",
        borderRadius: "8px",
        fontSize: "12px"
    },
    analysticContainer: {
        width: "35%",
        fontWeight: "600",
        position: "absolute",
        border: "2px solid #b3e5fc",
        right: "10px",
        top: "350px",
        // height: "635px",
